    border-radius: @sapTile_BorderCornerRadius;
    position: relative;
    padding: 1rem;
    box-shadow: @sapContent_Shadow0;
    background-color: @sapTile_Background;
    display: inline-block;
    box-sizing: border-box;
    user-select: none;
    vertical-align: top;
    text-decoration: none;
    .sapMGTHdrContent {
        padding: 0;
        height: 3rem;
        overflow: hidden;
        margin-bottom: 0.75rem;
        .sapMText{
            font-size: 1rem;
            font-weight: 700;
            color: @sapTile_TitleTextColor !important;
            line-height: 1.5rem;
            cursor: pointer;
        }
    }
    .sapMGTActionModeContainer {
        position: absolute;
        bottom: 0.9em;
        right: 1rem;
    }
    .sapMGTContent {
        height: 17.4rem;
        overflow: visible;
        .sapMTileCnt{
            display: flex;
            flex-direction: column;
            height: inherit;
            .sapMTileCntContent{
                margin-top: 0;
                padding: 0;
                .sapMFT{
                    height: inherit;
                    span{
                        color: @sapTextColor;
                        white-space: initial;
                        word-break: break-all;
                        display: -webkit-box;
                        -webkit-line-clamp: 16;
                        -webkit-box-orient: vertical;
                        height: fit-content;
                        line-height: 1rem;
                        overflow: hidden;
                        text-overflow: ellipsis;
                        font-weight: 400;
                        font-size: 0.875rem;
                        margin-bottom: 0.75rem;
                        max-height: 16rem;
                    }
                    p + span {
                        margin-bottom: 1rem;
                        -webkit-line-clamp: 14;
                        max-height: 14rem;
                    }
                    p {
                        white-space: nowrap;
                        overflow: hidden;
                        text-overflow: ellipsis;
                        color: @sapTextColor;
                        font-weight: 400;
                        font-size: 0.875rem;
                        margin: 0 0 1rem 0;
                        line-height: 1.125rem;
                    }
                    p.sapMbrPresent::first-line {
                        color: @sapContent_LabelColor;
                    }
                    p:not(.sapMbrPresent) {
                        margin-bottom: 0.75rem;
                        white-space: initial;
                        word-break: break-all;
                    }
                }
            }
            .sapMTileFtrCnt {
                position: absolute;
                bottom: 1rem;
                right: 0rem;
            }
        }
    }
    &.sapMATHideActionButton {
        .sapMTileCntContent {
            .sapMFT {
                span {
                    -webkit-line-clamp: 14 !important;
                }
                p + span {
                    -webkit-line-clamp: 12 !important;
                }
            }
        }
        .sapMGTActionModeContainer {
            display: none;
        }
    }
}

