.sapMLIBActionable,
.sapMLIBActionable > .sapMLIBImgNav,
.sapMListTblNavCol > .sapMLIBImgNav,
.sapMLIBActionable .sapMSLIThumbnail,
.sapMLIBActionable .sapMSLIImgIcon {
	cursor: pointer;
}

.sapMLIBImgNav,
.sapMLIBImgDet,
.sapMLIBImgDel,
.sapMLIBIconDet,
.sapMLIBSelectS,
.sapMLIBSelectM,
.sapMLIBSelectD,
.sapMLIBCounter {
	-webkit-box-flex: 0;
	flex: none;
}

.sapMLIBHighlight {
	position: absolute;
	width: 0.375rem;
	bottom: 0;
	left: 0;
	top: 0;
	border-right: 0.0625rem solid @sapUiListBackground;
