	font-size: 0.75rem;
	color: @sapUiContentLabelColor;
}

.sapMLIBActive .sapMLIBImgNav,
.sapMLIBActive .sapMLIBIconDet .sapMBtnIcon,
.sapMLIBActive .sapMLIBIconDel .sapMBtnIcon {
