/* START "NavContainer.less" */
/* =================================== */
/* CSS for control sap.m/NavContainer  */
/* Base theme                          */
/* =================================== */

.sapMNav {
	overflow: hidden;
	position: relative;
	height: 100%;
}

.sapMNavFlip {
	-webkit-perspective: 1000;
	perspective: 1000px;
}

.sapMNavItem,
.sapMNavItem.sapMPage {
	position: absolute;
	top: 0;
	left: 0;
	display: block;
	height: 100%;
}

.sapMNavItem.sapMNavItemRendering {
	visibility: hidden;
	left: -4000px;
}

.sapMNavItem.sapMNavItemHidden {
	display: none;
}

/* animations */
html[data-sap-ui-animation='on'] {

	.sapMNavItem,
	.sapMNavItem.sapMPage {
		-webkit-transition-property: -webkit-transform;
		transition-property: transform;
	}

	/* for slide animation */
	.sapMNavItem.sapMNavItemCenter {
		-webkit-transform: translate3d(0px, 0px, 0px);
		transform: translate3d(0px, 0px, 0px);
	}

	.sapMNavItem.sapMNavItemSlideLeft {
		-webkit-transform: translate3d(1rem, 0px, 0px);
		transform: translate3d(1rem, 0px, 0px);
	}

	.sapMNavItem.sapMNavItemSlideRight {
		-webkit-transform: translate3d(-1rem, 0px, 0px);
		transform: translate3d(-1rem, 0px, 0px);
	}

    /* for base slide animation */

	.sapMNavItem.sapMNavItemSlideRightToCenter {
		-webkit-animation: sapMNavItemAnimationRightToCenter .3s;
		animation: sapMNavItemAnimationRightToCenter .3s;
	}

    .sapMNavItem.sapMNavItemSlideCenterToLeft {
        -webkit-animation: sapMNavItemAnimationCenterToLeft .3s;
        animation: sapMNavItemAnimationCenterToLeft .3s;
    }

    @-webkit-keyframes sapMNavItemAnimationRightToCenter {
        0% {
            -webkit-transform: translate3d(100%, 0px, 0px);
        }
        100% {
            -webkit-transform: translate3d(0px, 0px, 0px);
        }
    }

    @-webkit-keyframes sapMNavItemAnimationCenterToLeft {
        0% {
            -webkit-transform: translate3d(0px, 0px, 0px);
        }
        100% {
            -webkit-transform: translate3d(-100%, 0px, 0px);
        }
    }

    @keyframes sapMNavItemAnimationRightToCenter {
        0% {
            transform: translate3d(100%, 0px, 0px);
        }
        100% {
            transform: translate3d(0px, 0px, 0px);
        }
    }

    @keyframes sapMNavItemAnimationCenterToLeft {
        0% {
            transform: translate3d(0px, 0px, 0px);
        }
        100% {
            transform: translate3d(-100%, 0px, 0px);
        }
    }

	/* for fade animation */

	.sapMNavItem.sapMNavItemTransparent {
		-webkit-transition-property: opacity, transform;
		transition-property: opacity, transform;
		opacity: 0;
	}

	.sapMNavItem.sapMNavItemOpaque {
		-webkit-transition-property: opacity, transform;
		transition-property: opacity, transform;
		opacity: 1;
		-webkit-transform: translate3d(0px, 0px, 0px);
		transform: translate3d(0px, 0px, 0px);
	}

	.sapMNavItem.sapMNavItemFading {
		-webkit-transition-duration: .15s;
		transition-duration: .15s;
	}

	/* for flip animation */

	.sapMNavItem.sapMNavItemFlipNext {
		-webkit-transform: rotateY(180deg);
		transform: rotateY(180deg);
		-webkit-backface-visibility: hidden;
		backface-visibility: hidden;
	}

	.sapMNavItem.sapMNavItemFlipPrevious {
		-webkit-transform: rotateY(-180deg);
		transform: rotateY(-180deg);
		backface-visibility: hidden;
	}

	.sapMNavItem.sapMNavItemFlipping {
		-webkit-transition-duration: .5s;
		transition-duration: .5s;
		-webkit-backface-visibility: hidden;
		backface-visibility: hidden;
	}

	/* for door animation */

	@-webkit-keyframes sapMNavItemDoorInPrevious {
		from {
			-webkit-transform: rotate3d(0, 1, 0, 0deg);
			visibility: visible;
			opacity: 1;
		}
		40% {
			-webkit-transform: rotate3d(0, 1, 0, -70deg);
			visibility: visible;
			opacity: 0;
		}
		41% {
			-webkit-transform: rotate3d(0, 1, 0, -70deg);
			visibility: hidden;
		}

		to {
			-webkit-transform: rotate3d(0, 1, 0, -70deg);
		}
	}

	@keyframes sapMNavItemDoorInPrevious {
		from {
			transform: rotate3d(0, 1, 0, 0deg);
			visibility: visible;
			opacity: 1;
		}
		40% {
			transform: rotate3d(0, 1, 0, -70deg);
			visibility: visible;
			opacity: 0;
		}
		41% {
			transform: rotate3d(0, 1, 0, -70deg);
			visibility: hidden;
		}

		to {
			transform: rotate3d(0, 1, 0, -70deg);
		}
	}

	@-webkit-keyframes sapMNavItemDoorInNext {
		from {
			-webkit-transform: rotate3d(0, 1, 0, 80deg);
			opacity: 0;
		}
		46% {
			-webkit-transform: rotate3d(0, 1, 0, 80deg);
			opacity: 0;
		}
		to {
			-webkit-transform: rotate3d(0, 1, 0, 0deg);
			opacity: 1;
		}
	}

	@keyframes sapMNavItemDoorInNext {
		from {
			transform: rotate3d(0, 1, 0, 80deg);
			visibility: hidden;
			opacity: 0;
		}
		45% {
			transform: rotate3d(0, 1, 0, 80deg);
			visibility: hidden;
			opacity: 0;
		}
		46% {
			transform: rotate3d(0, 1, 0, 80deg);
			visibility: visible;
			opacity: 0;
		}
		to {
			transform: rotate3d(0, 1, 0, 0deg);
			opacity: 1;
		}
	}

	@-webkit-keyframes sapMNavItemDoorOutPrevious {
		from {
			-webkit-transform: rotate3d(0, 1, 0, 0deg);
			visibility: visible;
			opacity: 1;
		}
		40% {
			-webkit-transform: rotate3d(0, 1, 0, 80deg);
			visibility: visible;
			opacity: 0;
		}
		41% {
			-webkit-transform: rotate3d(0, 1, 0, 80deg);
			visibility: hidden;
		}
		to {
			-webkit-transform: rotate3d(0, 1, 0, 80deg);
		}
	}

	@keyframes sapMNavItemDoorOutPrevious {
		from {
			transform: rotate3d(0, 1, 0, 0deg);
			visibility: visible;
			opacity: 1;
		}
		40% {
			transform: rotate3d(0, 1, 0, 80deg);
			visibility: visible;
			opacity: 0;
		}
		41% {
			transform: rotate3d(0, 1, 0, 80deg);
			visibility: hidden;
		}
		to {
			transform: rotate3d(0, 1, 0, 80deg);
		}
	}

	@-webkit-keyframes sapMNavItemDoorOutNext {
		from {
			-webkit-transform: rotate3d(0, 1, 0, -70deg);
			opacity: 0;
		}
		46% {
			-webkit-transform: rotate3d(0, 1, 0, -70deg);
			opacity: 0;
		}

		to {
			-webkit-transform: rotate3d(0, 1, 0, 0deg);
			opacity: 1;
		}
	}

	@keyframes sapMNavItemDoorOutNext {
		from {
			transform: rotate3d(0, 1, 0, -70deg);
			visibility: hidden;
			opacity: 0;
		}
		45% {
			transform: rotate3d(0, 1, 0, -70deg);
			visibility: hidden;
			opacity: 0;
		}
		46% {
			transform: rotate3d(0, 1, 0, -70deg);
			visibility: visible;
			opacity: 0;
		}

		to {
			transform: rotate3d(0, 1, 0, 0deg);
			opacity: 1;
		}
	}

	.sapMNavDoor {
		-webkit-perspective: 1000;
		perspective: 1000px;
		-webkit-perspective-origin: 0% 50%;
		perspective-origin: 0% 50%;
	}

	.sapMNavItem.sapMNavItemDoorInPrevious {
		-webkit-animation: sapMNavItemDoorInPrevious .8s;
		animation: sapMNavItemDoorInPrevious .8s;
		visibility: hidden;
		opacity: 0;
	}

	.sapMNavItem.sapMNavItemDoorInNext {
		-webkit-animation: sapMNavItemDoorInNext .8s;
		animation: sapMNavItemDoorInNext .8s;
		visibility: visible;
	}

	.sapMNavItem.sapMNavItemDoorOutPrevious {
		-webkit-animation: sapMNavItemDoorOutPrevious .8s;
		animation: sapMNavItemDoorOutPrevious .8s;
		visibility: hidden;
		opacity: 0;
	}

	.sapMNavItem.sapMNavItemDoorOutNext {
		-webkit-animation: sapMNavItemDoorOutNext .8s;
		animation: sapMNavItemDoorOutNext .8s;
		visibility: visible;
	}

	.sapMNavItem.sapMNavItemDooring {
		-webkit-animation-timing-function: ease-out;
		animation-timing-function: ease-out;
		-webkit-transform-origin: 0% 50%;
		transform-origin: 0% 50%;
	}

}
/* END "NavContainer.less" */

/* START "NewsContent.less" */
/* ================================== */
/* CSS for control sap.m/NewsContent  */
/* Base theme                         */
/* ================================== */

.sapMNwC {
	display: inline-block;
	outline: none;
	width: 100%;
	height: 3.5625rem;
	color: @sapUiTileTextColor;
	background-color: @sapUiTileBackground;
}

.sapMNwCCTxt {
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
	font-family: @sapUiFontFamily;
	font-weight: normal;
	padding: 0.1875rem 0 0 0;
	max-width: 20.5rem;

	.sapMText {
		display: inline;
		line-height: 1.1875rem;
	}

	.sapMText.sapMPointer * {
		cursor: pointer;
	}
}

.Stretch .sapMNwCCTxt {
	max-width: none;
	display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
    font-weight: bold;
}

.TwoByOne .sapMNwCCTxt {
	display: -webkit-box;
	-webkit-line-clamp: 2;
	-webkit-box-orient: vertical;
}

.Stretch{
	.sapMTileCnt.News .sapMNwC{
		.sapMNwCSbh{
			max-width: none;
			padding-top: 0.25rem;

			.sapMFT{
				word-wrap: break-word;
				white-space: normal;
				height: 2rem;
				overflow: hidden;
				max-width: 60ch;
				text-overflow: ellipsis;
				white-space: nowrap;
			}
		}
	}
}

.sapMNwCSbh {
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
	font-family: @sapUiFontFamily;
	font-weight: normal;
	max-width: 20.5rem;
	width: 100%;
	display: inline-block;
	line-height: 1.1875rem;
	color: @sapUiTileTextColor;
	font-size: @sapMFontMediumSize;
}

.sapMNwCCTxt .sapMText, .sapMNwCCTxt .sapMFT {
	font-size: @sapMFontMediumSize;
	color: @sapUiContentContrastTextColor;
	overflow-wrap: break-word;
	white-space: pre-line;
}

.sapMNwC:focus:not([tabindex]) {
	outline: none;
}

.sapMTileSmallPhone {
	.sapMNwC {
		height: 3.188rem;
	}

	.sapMNwCCTxt {
		max-width: 17.75rem;
		padding: 0;
		.sapMText {
			line-height: 1.063rem;
		}
	}

	&.sapMGTStretch{
		.sapMNwCCTxt{
			max-width: none;
		}
	}

	.sapMNwCSbh {
		max-width: 17.75rem;
		line-height: 1.063rem;
	}

	.sapMNwCCTxt .sapMText, .sapMNwCSbh {
		font-size: @sapMFontSmallSize;
	}

	.sapMTileSmallPhone.Stretch {
		.sapMNwCSbh {
			padding-top: 0.5rem;
		}
		.sapMNwCSbh > .sapMFT {
			white-space: nowrap;
			width: 11.063rem;
			overflow: hidden;
			max-width: 60ch;
			text-overflow: ellipsis;
			white-space: nowrap;
			text-overflow: ellipsis;
		}
	}
}
/* END "NewsContent.less" */

/* START "NotificationListBase.less" */
/* =========================================== */
/* CSS for control sap.m/NotificationListBase  */
/* Base theme                                  */
/* =========================================== */

.sapMNLIB {
	position: relative;

	&:focus {
		outline: none;
	}

	.sapMNLICloseBtn:not(:empty) {
		margin: 0 0.5rem;
	}
}
// Priority icons

.sapMNLIBPriority {
	padding-right: 0.625rem;

	.sapUiIcon {
		font-size: 1rem;
		line-height: 1rem;
	}
}

.sapMNLIBPriorityLow .sapUiIcon {
	color: @sapUiSuccessBorder;

	&::before {
		content: '\e1c1';
	}
}

.sapMNLIBPriorityMedium .sapUiIcon {
	color: @sapUiWarningBorder;

	&::before {
		content: '\e053';
	}
}

.sapMNLIBPriorityHigh .sapUiIcon {
	color: @sapUiErrorBorder;

	&::before {
		content: '\e1ec';
	}
}

.sapMNLIBHiddenButton {
	display: none;
}

.sapUiSizeCompact {
	.sapMNLIBPriority {
		padding-right: 0.5rem;
	}
}

/* END "NotificationListBase.less" */

/* START "NotificationListGroup.less" */
/* ============================================ */
/* CSS for control sap.m/NotificationListGroup  */
/* Base theme                                   */
/* ============================================ */

@_sap_m_NotificationListGroup_Background: @sapUiListGroupHeaderBackground;
@_sap_m_NotificationListGroup_HeaderColor: @sapUiListTableGroupHeaderTextColor;

.sapMNLGroup {
	&.sapMLIB {
		display: block;
		padding: 0;
		margin: 0;
		overflow: hidden;
		border-bottom: none;
	}

	&.sapMNLGroupCollapsed .sapMNLGroupChildren {
		display: none;
	}

	.sapMNLGroupHeader {
		position: relative;
		min-height: 2.75rem;
		background: @_sap_m_NotificationListGroup_Background;
		border-bottom: 0.0625rem solid @sapUiListGroupHeaderBorderColor;
		box-sizing: border-box;
		padding: 0.75rem 0.5rem 0 1rem;
		display: flex;
		align-items: flex-start;
		outline: none;

		.sapMNLIBPriority {
			padding-top: 0.75rem;
		}

		.sapMNLGroupTitle {
			padding: 0.75rem 0;
			color: @_sap_m_NotificationListGroup_HeaderColor;
			font-size: @sapMFontHeader6Size;
			font-family: @sapUiFontHeaderFamily;
			font-weight: bold;
			overflow-wrap: break-word;
			flex: 1;
			min-width: 0;
		}
	}

	&:focus .sapMNLGroupHeader::before {
		content: " ";
		border: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiContentFocusColor;
		position: absolute;
		top: 1px;
		right: 1px;
		bottom: 1px;
		left: 1px;
		z-index: 2;
		pointer-events: none;
	}

	.sapMNLGroupChildren {
		margin: 0;
		padding: 0;
	}

	.sapMNLGroupCollapseButton {
		margin-right: 0.75rem;
		width: 2.25rem;
	}

	.sapMNLGroupMaxNotifications {
		padding: 1rem;
		background: @sapUiListBackground;
		text-align: center;

		.sapMNLGroupMNTitle {
			font-family: @sapUiFontHeaderFamily;
			font-size: @sapMFontHeader5Size;
			font-weight: @sapUiFontHeaderWeight;
			color: @sapUiGroupTitleTextColor;
			margin-bottom: 0.5rem;
		}

		.sapMNLGroupMNDescription {
			font-family: @sapUiFontFamily;
			font-size: @sapMFontMediumSize;
			color: @sapUiContentLabelColor;
		}
	}
}

.sapMNLIActionsHidden {
	display: none;
}

.sapUiSizeCompact {
	.sapMNLGroup {
		.sapMNLGroupHeader {
			padding: 0.5rem 0.5rem 0 0.75rem;

			.sapMNLIBPriority {
				padding-top: 0.5rem;
			}

			.sapMNLGroupTitle {
				padding: 0.5rem 0;
			}
		}
	}
}
/* END "NotificationListGroup.less" */

/* START "NotificationListItem.less" */
/* =========================================== */
/* CSS for control sap.m/NotificationListItem  */
/* Base theme                                  */
/* =========================================== */

@_sap_m_NotificationListBase_ItemBackground: @sapUiListBackground;
@_sap_m_NotificationListBase_ItemGroupedBackground: @sapUiListBackground;

.sapMNLI {
	background: @_sap_m_NotificationListBase_ItemBackground;
	cursor: pointer;
	display: flex;
	flex-direction: column;
	padding: 1rem 0.5rem 1rem 1rem;

	.sapMMsgStrip  {
		margin-bottom: 1rem;
	}

	.sapMNLIMain {
		display: flex;
		flex-direction: row-reverse;
		width: 100%;
	}

	.sapMNLIItemAC {
		display: flex;
	}

	.sapMNLIImage {
		margin-right: 0.75rem;
	}

	.sapMNLIContent {
		display: flex;
		flex-direction: column;
		min-width: 0; // chrome flex overflow issue
		flex: 1;
		padding-right: 1rem;

		.sapMNLITitle {
			display: flex;
			align-items: flex-start;

			font-size: @sapMFontHeader6Size;
			font-weight: @sapUiFontHeaderWeight;
			line-height: 1.4;
			color: @sapUiGroupTitleTextColor;

			padding-bottom: 0.25rem;
		}

		.sapMNLIDescription {
			font-size: @sapMFontMediumSize;
			color: @sapUiBaseText;
			line-height: 1.4;
			margin-top: 0.5rem;
			word-wrap: break-word;

			&.sapMNLIDescriptionNoText {
				margin-top: 0;
			}
		}
	}

	.sapMNLIFooter {
		display: flex;
		align-items: center;

		padding-top: 0.5rem;

		.sapMNLIFooterItem {
			font-size: @sapMFontMediumSize;
			color: @sapUiContentLabelColor;

			padding-top: 2px;
			white-space: nowrap;
		}

		// the author name should truncate
		.sapMNLIFooterItem:first-child {
			overflow: hidden;
			text-overflow: ellipsis;
			white-space: nowrap;
		}

		.sapMNLIFooterBullet {
			margin: 0 0.5rem;
		}

		.sapMBtn {
			height: 2rem;
			box-sizing: border-box;
		}
	}

	&.sapMNLIUnread {
		.sapMNLIContent {
			.sapMNLITitle {
				font-weight: bold;
			}
		}
	}

	&.sapMNLINoAvatar .sapMNLIImage,
	&.sapMNLIB-LayoutS .sapMNLIImage {
		display: none;
	}

	&:focus::before {
		content: " ";
		border: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiContentFocusColor;
		position: absolute;
		top: 1px;
		right: 1px;
		bottom: 1px;
		left: 1px;
		z-index: 2;
		pointer-events: none;
	}
}

.sapMNLGroup .sapMNLI {
	background: @_sap_m_NotificationListBase_ItemGroupedBackground;
}

.sapMNLIItemTextLineClamp {
	display: -webkit-box;
	-webkit-line-clamp: 2;
	-webkit-box-orient: vertical;
	overflow: hidden;
	max-height: 40px; // font-size * line-height (value 1.4) * 2 (number of rows)
}

.sapMNLIShowMore .sapMLnk {
	vertical-align: middle;
	margin-left: 1rem;
}

.sapMNLITitleText {
	font-size: @sapMFontHeader6Size;
}

.sapMLIB.sapMNLI {
	align-items: normal;
}

.sapUiSizeCompact {
	.sapMNLI {
		padding: 1rem 0.5rem 1rem 0.75rem;
	}
}
/* END "NotificationListItem.less" */

/* START "NumericContent.less" */
/* ============================= */
/* CSS for sap.m/NumericContent  */
/* Base theme                    */
/* ============================= */

.sapMNC {
	position: relative;
	box-sizing: border-box;
	outline: none;

	height: 3.875rem;
	width: 9rem;

	&.WithoutMargin {
		width: 100%;
	}
}

.sapMNCAnimation {
	animation-name: sapMCOpacityAnimation;
	animation-duration: 1s;
}

@keyframes sapMCOpacityAnimation {
	0% {
		opacity: 0.25;
	}

	100% {
		opacity: 1;
	}
}

.sapMNCInner {
	position: absolute;
	bottom: 0;
	width: 100%;

	&.WithoutMargin {
		overflow: visible;
		white-space: nowrap;
		display: flex;
	}
}

td .sapMNCValue.WithoutMargin {
    justify-content: flex-end;
}

.sapMNCIconImage {
	display: flex;
	flex-direction: column;
	justify-content: center;
	padding-right: 0.375rem;
	font-family: @sapUiFontHeaderFamily;
	font-size: 1.75rem;
	font-weight: @sapUiFontHeaderWeight;
	color: @sapTile_IconColor;

	&.sapMNCLargeFontSize:before {
		padding-bottom: 0.25rem;
	}

	&.sapMNCMediumFontSize:before {
		padding-bottom: 0.25rem;
	}

	&.sapMNCSmallFontSize:before {
		padding-bottom: 0.25rem;
	}
}

.sapMNCValue {
	display: inline-flex;
	justify-content: center;
	position: relative;
	width: 100%;
	white-space: nowrap;
	text-align: right;
	text-overflow: ellipsis;
	font-size: 2.25rem;
	font-family: @sapFontLightFamily;
	font-weight: @sapUiFontHeaderWeight;

	&.WithoutMargin {
		justify-content: center;
	}
}

.sapMNCLargeFontSize {
	height: 2.875rem;
	padding-top: 0.125rem;

	&:not(.sapMNCIconImage) {
		font-size: 2.25rem;
	}
}

.sapMNCMediumFontSize {
	height: 2.5rem;
	padding-top: 0;

	&:not(.sapMNCIconImage) {
		font-size: 2rem;
	}
}

.sapMNCSmallFontSize {
	height: 2.25rem;
	padding-top: 0;

	&:not(.sapMNCIconImage) {
		font-size: 1.75rem;
	}
}

html[dir=rtl] .sapMNCValue {
	text-align: left;
}

.sapMNCIndIcon {
	grid-column: 1;
	grid-row: 1;
}

.sapMNCIndScale {
	display: grid;
	text-overflow: ellipsis;
	text-align: center;

	&.sapMNCLargeFontSize {
		height: 2.125rem;
		padding: 0.5rem 0 0.375rem 0.375rem;

		.sapMNCScale {
			margin-top: 0.9375rem;
		}
	}

	&.sapMNCMediumFontSize {
		height: 1.938rem;
		padding: 0.25rem 0 0.5rem 0.5rem;

		.sapMNCScale {
			margin-top: 0.875rem;
		}
	}

	&.sapMNCSmallFontSize {
		height: 1.938rem;
		padding: 0 0 0.313rem 0.5rem;

		.sapMNCScale {
			margin-top: 0.875rem;
		}
	}

	&.WithoutMargin {
		float: left;
	}
}

.sapMNCScale {
	font-size: var(--sapFontSize);
	font-family: @sapUiFontHeaderFamily;
	font-weight: @sapUiFontHeaderWeight;
	grid-column: 1;
	grid-row: 1;
}

.sapMListTblSubRowCell .sapMNCValue {
	display: contents;
}

.sapMNCValue.Neutral,
.sapMNCIndScale.Neutral,
.sapMNCScale.Neutral {
	color: @sapUiNeutralText;
}

.sapMNCValue.Good,
.sapMNCIndScale.Good,
.sapMNCScale.Good {
	color: @sapUiPositiveText;
}

.sapMNCValue.Error,
.sapMNCIndScale.Error,
.sapMNCScale.Error {
	color: @sapUiNegativeText;
}

.sapMNCValue.Critical,
.sapMNCIndScale.Critical,
.sapMNCScale.Critical {
	color: @sapUiCriticalText;
}

.sapMNCIndScale.Loading,
.sapMNCScale.Loading,
.sapMNCIconImage.Loading,
.sapMNCValue.Failed {
	color: @sapUiContentLabelColor;
	opacity: 0.25;
}

.sapMNC:focus {
	outline: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiContentFocusColor;
}

.sapMNC:focus:not([tabindex]) {
	outline: none;
}

.sapMTileSmallPhone {
	.sapMNC {
		height: 3.438rem;
		width: 8rem;
	}

	.sapMNCLargeFontSize {
		font-size: var(--sapFontHeader3Size);
		height: 1.875rem;
		padding-top: 0.5rem;
	}

	.sapMNCMediumFontSize {
		font-size: var(--sapFontHeader3Size);
		height: 1.875rem;
		padding-top: 0.5rem;
	}

	.sapMNCSmallFontSize {
		font-size: var(--sapFontHeader3Size);
		height: 1.875rem;
		padding-top: 0.5rem;
	}

	.sapMNCIndScale {
		padding: 0.25rem 0 0.25rem 0.375rem;

		&.sapMNCSmallFontSize {
			height: 2.25rem;
			padding-bottom: 0;
			padding-top: 0;

			.sapMNCIndIcon {
				padding-top: 0.3125rem;
			}

			.sapMNCScale {
				margin-top: 1.0625rem;
			}
		}

		.sapMNCIndIcon:only-child { // when there's no scale
			margin-top: 0.5rem;
		}

		.sapMNCScale {
			margin-top: 0.8125rem;
		}
	}

	.sapMGTHdrTxt > .sapMText {
		font-size: @sapMFontHeader6Size;
	}
}
.sapMNCContentShimmerPlaceholderItem {
	padding: 1rem 1rem 0 1rem;
	box-sizing: border-box;
	display: flex;
	position: relative;
	align-items: center;
	flex: 1 1 auto;

	.sapMNCContentShimmerPlaceholderRows {
		flex-grow: 1;
		.sapMNCContentShimmerPlaceholderItemHeader {
			height: 1rem;
			margin-bottom: 0.5rem;
			margin-top: 0.5rem;
		}
	}
}

.sapMNCLoadingShimmer {
	.sapMNCLoadingShimmerMixin();
}

.sapMNCLoadingShimmerMixin() {
	background-color: @sapUiContentPlaceholderloadingBackground;
	background-image: @sapUiContentPlaceholderloadingGradient;
	background-repeat: no-repeat;
	background-size: 800px 144px;
	position: relative;
	border-radius: 0.25rem;

	[data-sap-ui-animation-mode="full"] & {
		animation-duration: 2.5s;
		animation-fill-mode: forwards;
		animation-iteration-count: infinite;
		animation-name: placeHolderShimmer;
		animation-timing-function: linear;
	}

	[data-sap-ui-animation-mode="basic"] &,
	[data-sap-ui-animation-mode="minimal"] & {
		animation-duration: 8s;
		animation-fill-mode: forwards;
		animation-iteration-count: infinite;
		animation-name: placeHolderShimmer;
		animation-timing-function: linear;
	}
}

@keyframes placeHolderShimmer {
	0% {
		background-position: -468px 0;
	}

	100% {
		background-position: 468px 0;
	}
}

@keyframes placeHolderShimmer{
    0% {
      background-position: -468px 0;
    }
    100% {
      background-position: 468px 0;
    }
}
