html.sap-desktop .sapMObjectNumberActive:focus {

	outline: none;

	.sapMObjectNumberInner::after {
		pointer-events: none;
		display: block;
		content: "";
		position: absolute;
		top: -1px;
		left: 1px;
		bottom: 0px;
		right: 1px;
		height: 1.125rem;
		outline: 1px dotted @sapUiContentFocusColor;
	}

	&.sapMObjectNumberInverted .sapMObjectNumberInner::after {
		top: -1px;
		left: 1px;
		bottom: -1px;
		right: 1px;
		height: 1.125rem;
		outline: 1px dotted @sapUiContentFocusColor;
	}



	&.sapMObjectNumberLarge {

		.sapMObjectNumberInner::after {
			top: 0px;
			left: 1px;
			bottom: 0px;
			right: 1px;
			height: 2rem;
		}

		&.sapMObjectNumberInverted .sapMObjectNumberInner::after {
			top: 3px;
			left: 3px;
			bottom: 3px;
			right: 3px;
			height: 1.625rem;
