/* ================================ */
/* CSS for control sap.m/SlideTile  */
/* Base theme                       */
/* ================================ */

.sapMST {
	background: transparent;
	position: relative;
	display: inline-block;
	font-size: 0;
	border-radius: 0.25rem;
	outline: none;
	box-shadow: @sapUiShadowLevel0;

	.sapMSTOverflowHidden {
		overflow: hidden;
		position: relative;
		height: 100%;
		width: 100%;
		border-radius: 0.23rem 0.23rem 0.25rem 0.25rem;
	}

	&.sapMSTScopeActions {

		.sapMSTMoreIcon {
			height: 1rem;
			position: absolute;
			right: 0.75rem;
			bottom: 0.563rem;
			z-index: 1;
			color: @sapUiButtonIconColor;
			pointer-events: none;
			background-color: transparent;
			border: 0;
			.sapMBtnIcon{
				bottom: 0.6rem;
				left: 0.9rem;
				border: 0;
				color: @sapButton_Lite_TextColor;
			}
			&:focus {
				outline: none;
			}
		}

		.sapMSTRemoveButton {
			width: 1.375rem;
			height: 1.375rem;
			border-radius: 50%;
			padding: 0px;
			position: absolute;
			top: -0.5rem;
			right: -0.5rem;
			z-index: 3;
			border: 1px solid @sapUiButtonBorderColor;
			background-color: @sapUiButtonBackground;
			color: @sapUiButtonTextColor;
			overflow: visible;

			.sapMBtnFocusDiv {
				display: none;
			}

			.sapMBtnInner {
				width: 100%;
				height: 100%;
				min-width: 0px;
				border-radius: 50%;
				border: none;
				outline-offset: -2px;
			}

			.sapMBtnIcon {
				font-size: 0.75rem;
				margin: 0px;
				width: 100%;

				&:before {
					position: relative;
					top: -0.125rem;
				}
			}

			&:focus > .sapMFocusable {
				outline: none;
				border-radius: 50%;
			}

			/* The touch area has a different size and is slightly moved to the left and bottom */
			&:before {
				content: "";
				color: @sapUiButtonIconColor;
				position: absolute;
				top: -1px;
				bottom: -0.6875rem;
				left: -0.6875rem;
				right: -0.0625rem;
			}
		}
	}
}

