/* CSS for control sap.m/TimePickerClock  */
/* Base theme                             */
/* ====================================== */

// clock dial related classes

.sapMTPClock {
	position: relative;
	width: auto;
	padding: 0.5625rem;
	outline: none;
	display: none;
}

.sapMTPClock.sapMTPCActive {
	display: block;
}

.sapMTPCDial {
	position: relative;
	width: auto;
	display: block;
	border-radius: 100%;
	background-color: @sapUiLegendWorkingBackground;
	text-align: center;
	z-index: 1;
}

.sapMTPCDial::before {
	content: attr(data-label);
	display: flex;
	align-items: center;
	justify-content: center;
	position: absolute;
	font-family: @sapUiFontFamily;
	font-size: @sapMFontMediumSize;
	color: @sapUiContentLabelColor;
	top: 2.75rem;
	left: 2.75rem;
	bottom: 2.75rem;
	right: 2.75rem;
	border-radius: 100%;
	z-index: 2;
}

.sapMTPCInner .sapMTPCDial::before {
	background-color: @sapUiLegendWorkingBackground;
}

.sapMTPCDial::after {
	content: "";
	display: block;
	padding-bottom: 100%;
}

.sapMTPClockCover {
	position: absolute;
	top: 0.5625rem;
	left: 0.5625rem;
	bottom: 0.5625rem;
	right: 0.5625rem;
	border-radius: 100%;
	z-index: 10;
	touch-action: none;
}

// units related classes
.sapMTPCMidDot {
	display: block;
	box-sizing: border-box;
	width: 0.125rem;
	height: 0.1875rem;
	margin: 0 auto 0.375rem auto;
	border-radius: 100%;
	background-color: @sapUiFieldBorderColor;
}

.sapMTPCDot {
	display: block;
	box-sizing: border-box;
	width: 0.25rem;
	height: 0.3125rem;
	margin: 0 auto 0.25rem auto;
	border-radius: 100%;
	background-color: @sapUiFieldBorderColor;
}

.sapMTPCMarker {
	box-sizing: border-box;
	width: 0.25rem;
	height: 0.5625rem;
	margin: 0 auto;
	border-radius: 100%;
	background-color: @sapUiButtonSelectedBackground;
	border: 0.0625rem solid @sapUiButtonSelectedBorderColor;
}

.sapMTPCItem {
	position: absolute;
	top: 0px;
	left: 50%;
	display: inline-block;
	width: 2.75rem;
	height: 100%;
	z-index: 3;
	-webkit-touch-callout: none; // iOS Safari
	-webkit-user-select: none; // Safari
	-khtml-user-select: none; // Konqueror HTML
	-moz-user-select: none; // Old versions of Firefox
	-ms-user-select: none; // Internet Explorer/Edge
	user-select: none; // Chrome, Edge, Opera and Firefox

	&.sapMTPCMarker {
		z-index: 4;
	}
}

.sapMTPCNumber {
	box-sizing: border-box;
	display: inline-block;
	width: 2.75rem;
	height: 2.75rem;
	border-radius: 100%;
	line-height: 2.75rem;
	text-align: center;
	vertical-align: top;
	font-family: @sapUiFontFamily;
	font-size:@sapMFontMediumSize;
	color: @sapUiBaseText;

	&.sapMTPCNumberHover:not(.sapMTPCSelected) {
		background-color: @sapUiListHoverBackground;
	}
}

.sapMTPCInvisible {
	visibility: hidden;
}

.sapMTPCSelected {
	color: @sapUiButtonSelectedTextColor;
	background-color: @sapUiButtonSelectedBackground;
	border: 0.0625rem solid @sapUiButtonSelectedBorderColor;
}

// Degree related classes
.degreeMixIn(@class, @angle) {
	.@{class} {
		transform: translate(-50%) rotate(@angle);
		.sapMTPCNumber {
			transform: rotate(-@angle);
		}
	}

	html[dir=rtl] .@{class} {
		transform: translate(-50%) rotate(-@angle);
		.sapMTPCNumber {
			transform: rotate(@angle);
		}
	}
}

.sapMTPNow {
	margin-left: 0.5rem;
}

.sapUiSizeCompact {

	.sapMTPCItem {
		width: 2rem;
	}

	.sapMTPCNumber {
		width: 2rem;
		height: 2rem;
		line-height: 2rem;
	}

}

