	height: 1rem;
	border-radius: 1rem;
	border: 0.125rem solid @sapUiContentDragAndDropActiveColor;
	background: @sapUiWhiteBG;
	pointer-events: none;
	margin-top: -0.125rem;
}

.sapMPluginsColumnResizerSizeDetector {
	position: absolute;
	left: -1000px;
	visibility: hidden;
	display: flex;
	flex-direction: column;
}
