	.sapUILinkActionSheet > .sapUILinkActionSheetLink {
		padding-left:1rem;
	}

	.sapUILinkActionSheetButton {
		text-align: left;
	}

}

.sap-desktop .sapUILinkActionSheetLink:focus {
	outline: 1px dotted;
	outline-color: @sapUiContentFocusColor;
	outline-offset: -1px;
}

/* IE ignores outline-offset. Use an overlay. */
