	}
}

.sapTntNLPopup .sapTntNLISecondLevel .sapMText {
	padding: @_sap_tnt_NavigationList_PopupItemPadding;
}

.sapTntNLPopup .sapTntNLIFirstLevel .sapMText {
	margin: 0 1rem 0 0;
