/* List Container: */
/* Opening with a small height, for Popup to open even with too-tall a list. */
/* Exact height will be set via JavaScript, either to "auto", either to n times the height of an item. */
.sapUiMsgList {
	background-color: @sapUiExtraLightBG;
	border: 1px solid #3355A8;
	overflow: hidden;
	padding: 3px 8px 1px;
	width: auto;
	height: 20px; /* Workaround: Popup would not open if too tall! */
}

/* LIs */
.sapUiMsgListLi {
	display: table-row;
	white-space: nowrap;
}
/* END "MessageList.less" */

/* START "MessageToast.less" */
/* =================================================== */
/* CSS for control sap.ui.commons/MessageToast.control */
/* =================================================== */

.sapUiMsgToast {
	background-color: transparent;
	color: @sapUiText;
}

.sapUiMsgToastMsg {
	background-color: white;
	border: 1px solid #306898;
	border-radius: 3px;
	margin-bottom: -2px; /* For the bottom-arrow to overlap onto the border */
	padding: 2px 6px;
}

/* Priorities affect the coloring of the Toast */
.sapUiMsgTError .sapUiMsgToastMsg {
	background-color: #FBE9EB;
	border-color: #B00017;
}
.sapUiMsgTWarning .sapUiMsgToastMsg {
	background-color: #FFFCB5;
	border-color: #868557;
}

.sapUiMsgTSuccess .sapUiMsgToastMsg {
	background-color: #E7F2ED;
	border-color: #80BE87;
}

/* The toasted Message should be centered */
.sapUiMsgToast .sapUiMsg {
	display: block;
	text-align: center;
}

/* For now, message icons and details are not displayed */
.sapUiMsgToastMsg .sapUiMsgIcon,
.sapUiMsgToastMsg .sapUiMsgLnk {
	display: none;
}

/* Styling of the down-arrow is Type/Priority dependent */
.sapUiMsgToastArrow {
	background: url("../base/img/messagebar/toastarrow_multi.gif") no-repeat scroll 0px 0px transparent;
	display: block;
	height: 9px;
	width: 15px;
}

.sapUiMsgTError .sapUiMsgToastArrow {
	background-image: url("../base/img/messagebar/toastarrow_Error.gif");
}

.sapUiMsgTWarning .sapUiMsgToastArrow {
	background-image: url("../base/img/messagebar/toastarrow_Warning.gif");
}

.sapUiMsgTSuccess .sapUiMsgToastArrow {
	background-image: url("../base/img/messagebar/toastarrow_Success.gif");
}
/* END "MessageToast.less" */

/* START "Paginator.less" */
/* ============================================= */
/* CSS for control sap.ui.commons/Paginator      */
/* BASE CSS                                      */
/* ============================================= */

.sapUiPag {
	display: inline-block;
}

.sapUiPagAccDesc {
	display: none;
}

/* back/forward buttons */
.sapUiPagBtn {
	display: inline-block;
}

.sapUiPagBtn.sapUiLnkDsbl {
	color: @sapUiBaseText;
}

.sapUiPagFirst {
	margin-right: 6px;
}

.sapUiPagBack {
	background: url(../base/img/paginator/arrow_previous.gif) no-repeat;
	background-position: left top;
	padding-left: 14px;
}

.sapUiPagForward {
	background: url(../base/img/paginator/arrow_next.gif) no-repeat;
	background-position: right top;
	padding-right: 14px;
}

.sapUiPagLast {
	margin-left: 6px;
}

/* numbered pages */
.sapUiPag > ul {
	display: inline-block;
	padding: 0;
	vertical-align: top;
	margin: 0 6px 0;
}

.sapUiPagPage {
	display: inline-block;
	vertical-align: top;
	text-align: center;
	padding: 0 4px;
	border: 1px solid transparent;
	margin: 0;
	cursor: pointer;
}

.sapUiPag > .sapUiLnk,
.sapUiPag > .sapUiLnkDsbl,
.sapUiPagPage > .sapUiLnk,
.sapUiPagPage > .sapUiLnkDsbl {
	line-height: normal;
	text-decoration: none;
}

.sapUiPagCurrentPage {
	border: 1px solid @sapUiMediumBorder;
}

.sapUiPagPage.sapUiPagCurrentPage {
	cursor: default;
}

.sapUiPagCurrentPage > .sapUiLnkDsbl {
	color: @sapUiBaseText;
}
/* END "Paginator.less" */

/* START "Panel.less" */
@_sap_ui_commons_Panel_BorderRadius: 0;

.sapUiPanel {
	display: inline-block;
	position: relative;
	vertical-align: top;
	box-sizing: border-box;
	border: 1px solid @sapUiMediumBorder;
	border-radius: @_sap_ui_commons_Panel_BorderRadius @_sap_ui_commons_Panel_BorderRadius 0 0;
}

.sapUiPanel:focus {
	outline: 1px dotted @sapUiFocus;
}

.sapUiPanel.sapUiPanelColl,
.sapUiPanelColl > .sapUiPanelHdr  {
	border-radius: @_sap_ui_commons_Panel_BorderRadius;
}

.sapUiPanelHdr {
	border-radius: @_sap_ui_commons_Panel_BorderRadius @_sap_ui_commons_Panel_BorderRadius 0 0;
	display: -webkit-box;
	display: flex;
	-webkit-box-orient: horizontal;
	-webkit-box-align: center;
	flex-direction: row;
	align-items: center;
	box-sizing: border-box;
	background-color: @sapUiExtraLightBG;
	white-space: nowrap;
	width: 100%; /* the minimum width is set through JS calculations */
	vertical-align: top;
	min-height: 23px;
	font-weight: bold;
}

.sapUiPanelDis > .sapUiPanelHdr {
	color: @sapUiContentDisabledTextColor;
}

/* items inside the Panel header */
.sapUiPanelHdrItem,
.sapUiPanelHdrRightItem {
	display: block;
	box-sizing: border-box;
}

.sapUiPanelCollArrow,
.sapUiPanelCollIco {
	width: 16px;
	height: 16px;
	line-height: 14px;
	cursor: pointer;
	background-repeat: no-repeat;
	background-position: center center;
	text-decoration: none;
}

.sapUiPanelCollArrow {
	/* the 'collapse' icon on the left-hand side of the panel header */
	background-image: url(../base/img/panel/expanded.gif);
}
.sapUiPanelColl .sapUiPanelCollArrow {
	background-image: url(../base/img/panel/collapsed.gif);
}

.sapUiPanelCollIco {
	display: none;
}

.sapUiPanelIco {
	border: 0;
	margin: 0;
	padding: 0 8px 0 4px;
}

.sapUiPanelTitle {
	margin: 0; /* remove the H1 default style */
	padding: 0;
	font-size: 11px;
	-webkit-box-flex: 1;
	flex-grow: 1;
	overflow: hidden;
	text-overflow: ellipsis;
	margin-right: 10px;
	min-width: 30px; /* the minimum width is also set to 30px through JS calculations */
	white-space: nowrap;
}

.sapUiPanelTb {
	-webkit-box-flex: 1000;
	flex-grow: 1000;
	min-width: 30px; /* the minimum width is also set to the width of the largest button through JS calculations */
	margin: 2px 10px 0 10px;
	white-space: normal;
}

.sapUiPanelTb.sapUiTbDesignFlat {
	background: none;
}

.sapUiPanelTb.sapUiTbDesignFlat > .sapUiBtn {
	margin: 0 2px 2px 0;
}

/* Panel content */
.sapUiPanelCont {
	border-top-style: solid;
	border-top-width: 1px;
	border-top-color: @sapUiMediumBorder;
	display: block;
	overflow: auto;
	box-sizing: border-box;
}

.sapUiPanelWithPadding > .sapUiPanelCont {
	padding: 6px;
}

.sapUiPanelHeightSet > .sapUiPanelCont {
	position: absolute;
	left: 0;
	right: 0;
	bottom: 0;
	top: 24px;
}

.sapUiPanelColl > .sapUiPanelCont {
	display: none;
}

/* IE only - because no FlexBoxLayout supported */

/* Feature detection is not used yet because we do not write the detected features to the DOM yet. Be assured it will come. */
html[data-sap-ui-browser*="ie"] .sapUiPanelHdr {
	white-space: nowrap;
	overflow: hidden;
	display: inline-block;
	position: relative;
}

html[data-sap-ui-browser*="ie"] .sapUiPanelHdrItem,
html[data-sap-ui-browser*="ie"] .sapUiPanelHdrRightItem {
	white-space: normal;
	display: inline-block;
	vertical-align: middle;
}

html[data-sap-ui-browser*="ie"] .sapUiPanelTitle {
	white-space: nowrap;
	height: 20px;
	line-height: 20px;
}

html[data-sap-ui-browser*="ie"] .sapUiPanelCollIco {
	position: absolute;
	top: 2px;
	right: 4px;
	display: none;
}

html[data-sap-ui-browser*="ie"] .sapUiPanelTb {
	right: 56px;
	min-width: 150px;
}

.sapUiPanelAreaDesignFill > .sapUiPanelCont {
	background-color: @sapUiExtraLightBG;
}

.sapUiPanelAreaDesignPlain > .sapUiPanelCont {
	background-color: @sapUiWhiteBG;
}

.sapUiPanel.sapUiPanelBorderDesignNone {
	border: none;
}

/* END "Panel.less" */

/* START "ProgressIndicator.less" */
/* ======================================================== */
/* CSS for control sap.ui.commons/ProgressIndicator.control */
/* ======================================================== */

.sapUiProgInd {
	display: inline-block;
}

.sapUiProgIndBorder {
	display: inline-block;
	border: 1px solid @sapUiContentForegroundBorderColor;
	border-radius: 3px;
	box-sizing: border-box;
}

.sapUiProgIndBar {
	position: relative;
	background-color: lighten(desaturate(@sapUiHighlight, 49), 44);
	color: contrast(lighten(desaturate(@sapUiHighlight, 49), 44), @sapTextColor, @sapUiTextInverted);
	border-radius: 3px;
	box-sizing: border-box;
	height: 14px;
	-moz-user-select: none;
	user-select: none;
}

.sapUiProgIndBarPos {
	position: relative;
	background-color: @sapUiSuccessBG;
	color: contrast(@sapUiSuccessBG, @sapTextColor, @sapUiTextInverted);
	border-radius: 3px;
	box-sizing: border-box;
	height: 14px;
	-moz-user-select: none;
	user-select: none;
}

.sapUiProgIndBarNeg {
	position: relative;
	background-color: @sapUiErrorBG;
	color: contrast(@sapUiErrorBG, @sapTextColor, @sapUiTextInverted);
	border-radius: 3px;
	box-sizing: border-box;
	height: 14px;
	-moz-user-select: none;
	user-select: none;
}

.sapUiProgIndBarCrit{
	position: relative;
	background-color: @sapUiWarningBG;
	color: contrast(@sapUiWarningBG, @sapTextColor, @sapUiTextInverted);
	border-radius: 3px;
	box-sizing: border-box;
	height: 14px;
	-moz-user-select: none;
	user-select: none;
}

.sapUiProgIndFont{
	display: inline-block;
	position: absolute;
	left: 0;
	top: 0;
	height: 14px;
	padding-left: 5px;
	vertical-align: top;
	font-size: 11px;
	white-space: nowrap;
}

.sapUiProgIndEndHidden{
	display: inline-block;
	width: 1px;
	height: 14px;
	visibility: hidden;
}

.sapUiProgIndEnd{
	display: inline-block;
	width: 1px;
	height: 14px;
	visibility: visible;
	background-color: @sapUiContentForegroundBorderColor;
}

.sapUiProgIndPosEnd{
	display: inline-block;
	width: 1px;
	height: 14px;
	visibility: visible;
	background-color: @sapUiContentForegroundBorderColor;
}

.sapUiProgIndNegEnd{
	display: inline-block;
	width: 1px;
	height: 14px;
	visibility: visible;
	background-color: @sapUiContentForegroundBorderColor;
}

.sapUiProgIndCritEnd {
	display: inline-block;
	width: 1px;
	height: 14px;
	visibility: visible;
	background-color: @sapUiContentForegroundBorderColor;
}
/* END "ProgressIndicator.less" */

/* START "RadioButton.less" */
.sapUiRb {
	vertical-align: top;
	line-height: @sapUiLineHeight;
	display: inline-block;
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}

/* If control is used on black/dark background */
.sapUiInverted-CTX .sapUiRb {
	color: @sapUiTextInverted;
}

/* Do not invert for table */
.sapUiInverted-CTX .sapUiTable .sapUiRb {
	color: @sapUiBaseText;
}

.sapUiRb > label {
	vertical-align: top;
	height: @sapUiLineHeight;
}

/* disabled */
.sapUiRbDis > label {
	color: @sapUiContentDisabledTextColor;
}

/* make outline on control borders because otherwise it is cut in layout containers using overflow: hidden */
.sapUiRb:focus{
	outline: 1px dotted @sapUiBaseBorder;
	outline-offset: -1px;
}
/* END "RadioButton.less" */

/* START "RadioButtonGroup.less" */

/* ======================================================= */
/* CSS for control sap.ui.commons/RadioButtonGroup.control */
/* ======================================================= */

.sapUiRbG:not(.sapUiRbGTab){
	display: inline-block;
}

.sapUiRbGTab{
	display: -webkit-box;
	display: -webkit-flex;
	display: flex;
}

.sapUiRbGCol{
	vertical-align: top;
	min-width: 10px;
	margin-right: 15px;
	overflow: hidden; /* needed in Safari */
}

.sapUiRbGTabFlex > .sapUiRbGCol{
	flex-grow: 1;
	-webkit-flex-grow: 1;
}

.sapUiRbGDummy{
	flex-grow: 1000;
	-webkit-flex-grow: 1000;
	-webkit-box-flex: 1000;
	min-width: 1px;
}

.sapUiRbG > .sapUiRb,
.sapUiRbGCol > .sapUiRb{
	float: left;
	clear: left;
	text-overflow: ellipsis;
	max-width: 100%;
}

.sapUiRbG.sapUiRbG1Row > .sapUiRb{
	float: none;
	clear: none;
	margin-right: 15px;
}

.sapUiRbGCol > .sapUiRb{
	width: 100%;
}

/* END "RadioButtonGroup.less" */

