/* START "RichTooltip.less" */
.sapUiRtt {
	font-family: @sapUiFontFamily;
	font-size: @sapUiFontSize;
	color: @sapUiBaseText;
	background-color: @sapUiExtraLightBG;
	position: relative;
	display: table;
}

.sapUiRtt > div {
	display: table-row;
}

.sapUiRtt > div > div {
	display: table-cell;
}

.sapUiRttTopL,
.sapUiRttTopR {
	display: none;
}

.sapUiRttContent {
	min-width: 250px;
	padding: 6px 8px;
}

.sapUiRttContentWide,
.sapUiRttContentWide .sapUiRttContent {
	min-width: 355px;
}

.sapUiRttTitle {
	padding: 2px 6px;
	font-weight: bold;
}

.sapUiRttSep {
	height: 1px;
	background-color: @sapUiMediumBorder;
}

.sapUiRttContentContainer {
	padding: 6px 6px 6px 8px;
}

.sapUiRttImage {
	display: inline-block;
	vertical-align: top;
	margin-right: 6px;
}

.sapUiRttText {
	display: inline-block;
	max-width: 250px;
	vertical-align: top;
	word-wrap: break-word;
}

.sapUiRttValueStateContainer {
	min-height: 16px;
	padding: 3px 6px 3px 8px;
	overflow: hidden;
}

.sapUiRttValueStateImage {
	display: inline-block;
	margin-right: 3px;
	height: 16px;
	float: left;
}

.sapUiRttValueStateText {
	display: inline-block;
	max-width: 250px;
	vertical-align: top;
	word-wrap: break-word;
	min-height : 16px;
	margin-top: 1px;
}

.sapUiRttBotL,
.sapUiRttBotR {
	display: none;
}
/* END "RichTooltip.less" */

/* START "RoadMap.less" */
/* ============================================== */
/* CSS for control sap.ui.commons/RoadMap.control */
/* ============================================== */

/* General Styles */
.sapUiRoadMap {
	display: inline-block;
	white-space: nowrap;
	overflow: hidden;
	position: relative;
}

.sapUiRoadMap:focus {
	outline: 0px dotted transparent;
}

.sapUiRoadMapStepArea {
	display: inline-block;
	overflow: hidden;
	white-space: nowrap;
	position: relative;
	margin: 0;
	padding: 1px 0 0;
}

.sapUiRoadMapContent {
	border-style: none;
	display: inline-block;
	text-align: center;
	vertical-align: top;
	white-space: normal;
}

.sapUiRoadMapHidden {
	display: none;
}

/* Step Styles */
.sapUiRoadMapStep {
	width: 98px;
	position: relative;
}

.sapUiRoadMapSubStep {
}

.sapUiRoadMapSubStep.sapUiRoadMapStep {
	padding-top: 4px;
}

.sapUiRoadMapStepEnd {
}

.sapUiRoadMapSelected {
	font-weight: bold;
}

.sapUiRoadMapExpanded {
}

.sapUiRoadMapDisabled {
	color: @sapUiContentDisabledTextColor;
}

.sapUiRoadMapStepAdd1,
.sapUiRoadMapStepAdd2 {
	display: inline-block;
	position: absolute;
	height: 1px;
	width: 37px;
	background-color: @sapUiBlackBG;
	top: 11px;
}

.sapUiRoadMapStepAdd1 {
	left: 0px;
}

.sapUiRoadMapStepAdd2 {
	right: 0px;
}

.sapUiRoadMapStepEnd.sapUiRoadMapExpanded > .sapUiRoadMapStepAdd1,
.sapUiRoadMapExpanded > .sapUiRoadMapStepAdd2,
.sapUiRoadMapSubStep > .sapUiRoadMapStepAdd1,
.sapUiRoadMapSubStep > .sapUiRoadMapStepAdd2 {
	top: 21px;
}

.sapUiRoadMapStepEnd.sapUiRoadMapExpanded > .sapUiRoadMapStepAdd2 {
	top: 11px;
}

.sapUiRoadMapSubStep > .sapUiRoadMapStepAdd1,
.sapUiRoadMapSubStep > .sapUiRoadMapStepAdd2 {
	width: 39px;
}

.sapUiRoadMapStepBox {
	border: 1px solid black;
	cursor: pointer;
	line-height: 20px;
	width: 22px;
	margin: auto;
	position: relative;
}

.sapUiRoadMapStepBox:focus{
	outline: 1px dotted @sapUiBlackBorder;
}

.sapUiRoadMapSubStep.sapUiRoadMapStep > .sapUiRoadMapStepBox{
	line-height: 16px;
	width: 19px;
}

.sapUiRoadMapSelected > .sapUiRoadMapStepBox{
	border: 1px solid @sapUiFieldInvalidColor;
	cursor: default;
}

.sapUiRoadMapDisabled > .sapUiRoadMapStepBox{
	border: 1px solid @sapUiButtonDisabledBorderColor;
	cursor: default;
}

.sapUiRoadMapStepIco {
	display: none;
}

.sapUiRoadMapTitle {
	display: inline-block;
	width: 90px;
	overflow: hidden;
	/*text-overflow: ellipsis;*/
	word-wrap: break-word;
	height: 38px;/*double line-height*/
}

.sapUiRoadMapTitle > span:after {
	content: "...";
}

.sapUiRoadMapStep:hover > .sapUiRoadMapTitle {
	text-decoration: underline;
}

.sapUiRoadMapStep.sapUiRoadMapDisabled:hover > .sapUiRoadMapTitle {
	text-decoration: none;
}

.sapUiRoadMapStep.sapUiRoadMapSelected:hover > .sapUiRoadMapTitle {
	text-decoration: none;
}

/* No special styling for Expanded Step titles according to UX
.sapUiRoadMapExpanded > .sapUiRoadMapTitle {
	position: relative;
	left: 15px;
	text-align: left;
	width: 55px;
}

html[dir=rtl] .sapUiRoadMapExpanded > .sapUiRoadMapTitle {
	text-align: right;
}

.sapUiRoadMapExpanded.sapUiRoadMapStepEnd > .sapUiRoadMapTitle{
	left: auto;
	right: 15px;
	text-align: right;
}

html[dir=rtl] .sapUiRoadMapExpanded.sapUiRoadMapStepEnd > .sapUiRoadMapTitle {
	text-align: left;
}*/

/* Delimiter Styles */
.sapUiRoadMapDelim {
	background-position: center 4px;
	background-repeat: no-repeat;
	height: 18px;
	width: 17px;
	margin-top: 1px;
}

.sapUiRoadMapDelim:focus {
	outline: 0px dotted transparent;
}

.sapUiRoadMapStartScroll {
	cursor: pointer;
}

.sapUiRoadMapStartScroll:after {
	content: "<<";
}

.sapUiRoadMapStartFixed:after{
	content: "|>";
}

.sapUiRoadMapEndScroll {
	cursor: pointer;
}

.sapUiRoadMapEndScroll:after {
	content: ">>";
}

.sapUiRoadMapEndFixed:after {
	content: "<|";
}
/* END "RoadMap.less" */

/* START "RowRepeater.less" */
/* ================================================== */
/* CSS for control sap.ui.commons/RowRepeater.control */
/* ================================================== */

/* RowRepeater root

Three possible designs with corresponding CSS classes:
- Standard: sapUiRrDesignStandard
- Transparent: sapUiRrDesignTransparent
- BareShell: sapUiRrDesignBareShell
*/

/** Common Design **/

/* Primary Toolbar */
.sapUiRrPtb {
	display: table;
	width: 100%;
	border-top: 1px solid;
	border-left: 1px solid;
	border-right: 1px solid;
}

/* Primary Toolbar: Logo + Title */
.sapUiRrLogo {
	display: table-cell;
	padding-left: 10px;
}

.sapUiRrTitle {
	display: table-cell;
	white-space: nowrap;
	font-size: @sapUiFontLargeSize;
	font-weight: bold;
	vertical-align: middle;
}

/* Primary Toolbar - Filters */
.sapUiRrFilters {
	display: table-cell;
	width: 100%;
	vertical-align: middle;
	font-size: @sapUiFontLargeSize;
}

/* Primary Toolbar - Controller */
.sapUiRrCtrl {
	display: table-cell;
	white-space: nowrap;
	vertical-align: middle;
}

/* Secondary Toolbar */
.sapUiRrStb {
	width: 100%;
	display: table;
	border-top: 1px solid;
	border-left: 1px solid;
	border-right: 1px solid;
}

/* Secondary Toolbar - Sort By */
.sapUiRrSortBy {
	display: table-cell;
	white-space: nowrap;
	font-weight: bold;
	padding-left: 10px;
	vertical-align: middle;
}

/* Secondary Toolbar - Sorters */
.sapUiRrSorters {
	width: 100%;
	display: table-cell;
}

/* Row Container */
.sapUiRrBody {
	width: 100%;
	border: 1px solid;
	background-color: transparent;
	overflow: hidden;
	position: relative;
}

/* Page */
.sapUiRrPage {
	list-style: none;
	padding-left: 0px;
	position: relative;
	margin-top: 0px;
	margin-bottom:0px;
}

/* Row */
.sapUiRrRow {
}

/* No Data - Special Row */
.sapUiRrNoData {
	font-size: @sapUiFontLargeSize;
	font-weight: bold;
	margin: 10px;
}

/* Footer */
.sapUiRrFtr {
	width: 100%;
	border-bottom: 1px solid;
	border-left: 1px solid;
	border-right: 1px solid;
	height: 22px;
	text-align: center;
}

.sapUiRrFtr > .sapUiPag {
	padding-top: 3px;
}

/** Standard Design **/
.sapUiRrDesignStandard > .sapUiRrPtb {
	background-color: @sapUiExtraLightBG;
	border-color: @sapUiMediumBorder;
}

.sapUiRrDesignStandard > .sapUiRrStb {
	background-color: @sapUiDarkBG;
	border-color: @sapUiMediumBorder;
}

.sapUiRrDesignStandard > .sapUiRrFtr {
	background-color: @sapUiDarkBG;
	border-color: @sapUiMediumBorder;
}

.sapUiRrDesignStandard > .sapUiRrBody {
	border-color: @sapUiMediumBorder;
}

/** Transparent Design **/
.sapUiRrDesignTransparent > .sapUiRrPtb {
	background-color: transparent;
	border: none;
}

.sapUiRrDesignTransparent > .sapUiRrStb {
	background-color: transparent;
	border: none;
}

.sapUiRrDesignTransparent .sapUiTb {
	background-image: none;
}

.sapUiRrDesignTransparent > .sapUiRrFtr {
	background-color: transparent;
	border: none;
}

.sapUiRrDesignTransparent > .sapUiRrBody {
	border: none;
}

/** BareShell Design **/
.sapUiRrDesignBareShell > .sapUiRrBody {
	border: none;
}
/* END "RowRepeater.less" */

/* START "SearchField.less" */
/* ======================================================= */
/* Base CSS for control sap.ui.commons/SearchField.control */
/* ======================================================= */

.sapUiSearchField {
	display: inline-block;
	position: relative;
	vertical-align: top;
	width: 22ex;
}

.sapUiSearchFieldTf {
	box-sizing: border-box;
	vertical-align: top;
	overflow: visible;
	display: inline-block;
	outline: none;
	padding: 0 18px 0 0;
	margin-top: 1px;
}

.sapUiSearchField input[type="search"]::-webkit-search-cancel-button {
	/* Do not show the 'X' in the searchfield */
	-webkit-appearance: none;
}

.sapUiSearchFieldCb {
	padding: 0 30px 0 0;
}

/*The cursor position in Safari on Mac in LTR case is to far on the right*/
html[data-sap-ui-browser^="sf"][data-sap-ui-os="mac"]:not([dir=rtl]) .sapUiSearchField > .sapUiSearchFieldTf > .sapUiTf,
html[data-sap-ui-browser^="sf"][data-sap-ui-os="mac"]:not([dir=rtl]) .sapUiSearchField > .sapUiSearchFieldCb > .sapUiTf {
	margin-left: -4px;
	padding-left: 0px;
}

html[data-sap-ui-browser^="cr"][data-sap-ui-os="mac"]:not([dir=rtl]) .sapUiSearchField > .sapUiSearchFieldCb.sapUiSearchFieldCbProv > .sapUiTf,
html[data-sap-ui-browser^="sf"][data-sap-ui-os="mac"]:not([dir=rtl]) .sapUiSearchField > .sapUiSearchFieldCb.sapUiSearchFieldCbProv > .sapUiTf {
	padding-left: 16px;
}

.sapUiSearchFieldTf, .sapUiSearchFieldCb {
	width: auto;
	position: absolute;
	left: 0;
	right: 0;
	min-width: 50px;
}

.sapUiSearchFieldNoExp .sapUiSearchFieldCb,
.sapUiSearchFieldCb.sapUiTfRo {
	padding: 0 18px 0 0;
}

.sapUiSearchFieldCb .sapUiTfComboIcon {
	color: inherit;
	font-size: inherit;
}

.sapUiSearchFieldIco {
	overflow: visible;
	display: inline;
	position: absolute;
	font-size: 0;
	color: transparent;
	-moz-user-select: none;
	-webkit-user-select: none;
	background-image: url("../base/img/search/search.png");
	height: 15px;
	width: 15px;
	background-repeat: no-repeat;
	background-position: top right;
	background-color: transparent;
	margin-top: 3px;
	cursor: pointer;
}

.sapUiSearchFieldClear.sapUiSearchFieldVal.sapUiSearchFieldDsbl > .sapUiSearchFieldTf > .sapUiSearchFieldIco,
.sapUiSearchFieldClear.sapUiSearchFieldVal.sapUiSearchFieldDsbl > .sapUiSearchFieldCb > .sapUiSearchFieldIco {
	font-size: 0;
	color: transparent;
	background-image: url("../base/img/search/search.png");
}

.sapUiSearchFieldProvIco {
	display: inline;
	position: absolute;
	height: 16px;
	width: 16px;
	left: 1px;
	margin-top: 2px;
}

.sapUiSearchFieldProvIco > img {
	height: 16px;
	width: 16px;
}

.sapUiSearchFieldCb.sapUiSearchFieldCbProv > .sapUiTf {
	padding-left: 20px;
}

.sapUiSearchFieldDsbl .sapUiSearchFieldProvIco,
.sapUiSearchFieldDsbl .sapUiSearchFieldIco{
	cursor: auto;
	opacity: 0.6;
}

.sapUiSearchFieldTf .sapUiSearchFieldIco {
	right: 1px;
}

.sapUiSearchFieldCb .sapUiSearchFieldIco {
	right: 13px;
}

.sapUiSearchFieldNoExp .sapUiSearchFieldCb .sapUiSearchFieldIco {
	right: 1px;
}

.sapUiSearchFieldCb.sapUiTfRo .sapUiSearchFieldIco {
	right: 1px;
}

.sapUiSearchField > .sapUiBtn {
	position: absolute;
	margin-left: 5px;
	right: 0;
}

.sapUiSearchFieldClear.sapUiSearchFieldVal > .sapUiSearchFieldTf > .sapUiSearchFieldIco,
.sapUiSearchFieldClear.sapUiSearchFieldVal > .sapUiSearchFieldCb > .sapUiSearchFieldIco {
	font-size: inherit;
	color: inherit;
	background-image: none;
}

/* END "SearchField.less" */

/* START "SegmentedButton.less" */
/* ====================================================== */
/* CSS for control sap.ui.commons/SegmentedButton.control */
/* ====================================================== */

.sapUiSegmentedButton {
	color: @sapUiBaseText;
	white-space: nowrap;
}

.sapUiSegmentedButton > span > .sapUiBtnS {
	border-radius: 0;
	border-left-width: 0;
}

.sapUiSegmentedButton > span > button:first-of-type {
	border-radius:  2px 0 0 2px;
	border-left-width: 1px;
}

.sapUiSegmentedButton > span> button:last-of-type {
	border-radius: 0 2px 2px 0;
}

.sapUiSegmentedButton > span > .sapUiBtnS.sapUiSegButtonSelected {
	cursor: default;
	background-color: @sapUiHighlight;
	color: @sapUiTextSelection;
}

.sapUiSegmentedButton > span > .sapUiBtnS.sapUiBtnStd:focus,
.sapUiSegmentedButton > span > .sapUiBtnS.sapUiBtnStd.sapUiBtnStdFocus {
	outline: 1px dashed @sapUiHighlight;
}
/* END "SegmentedButton.less" */

/* START "SimpleForm.less" */
/* ====================================================== */
/* CSS for control sap.ui.commons/form/SimpleForm.control */
/* ====================================================== */
/* END "SimpleForm.less" */

/* START "Slider.less" */
/* ================================================= */
/* CSS for control sap.ui.commons/Slider.control     */
/* ================================================= */

/* All DIV elements MUST have a POSITION definition, otherwise the calculation of the
   grip position will not work */

.sapUiSli{
	position: relative;
	display: inline-block;
	overflow: visible;
	-moz-user-select: none;
	-webkit-user-select: none;
	border: 1px solid @sapUiMediumBorder;
}

.sapUiSliL,
.sapUiSliR,
.sapUiSliBar,
.sapUiSliHiLi {
	position: relative;
	display: block;
	outline: none;
}

.sapUiSliHiLi {
	position: absolute;
}

.sapUiSliHori .sapUiSliHiLi {
	left: 0;
}

.sapUiSliVert .sapUiSliHiLi {
	bottom: 0;
}

.sapUiSliHori .sapUiSliBar {
	height: 15px;
}

.sapUiSliVert .sapUiSliBar {
	width: 15px;
}

.sapUiSliGrip {
	position: absolute;
	display: inline-block;
	font-size: 0;
	color: transparent;
	border: 2px solid @sapUiMediumBorder;
}

.sapUiSliHori .sapUiSliGrip {
	height: 11px;
	width: 6px;
}

.sapUiSliVert .sapUiSliGrip {
	width: 11px;
	height: 6px;
}

.sapUiSliStd .sapUiSliGrip {
	cursor: pointer;
}

.sapUiSliDsbl .sapUiSliGrip:focus {
	outline: none; /* needed in safari */
}

.sapUiSliTick,
.sapUiSliText {
	position: absolute;
	display: inline-block;
}

.sapUiSliStd .sapUiSliText {
	color: @sapUiBaseText;
}

.sapUiSliRo .sapUiSliText,
.sapUiSliDsbl .sapUiSliText {
	color: @sapUiContentDisabledTextColor;
}

.sapUiSliVert .sapUiSliBar,
.sapUiSliVert .sapUiSliR,
.sapUiSliVert .sapUiSliL {
	position: absolute;
	top: 0;
	bottom: 0;
}
/* END "Slider.less" */

/* START "Splitter.less" */
/* =============================================== */
/* CSS for control sap.ui.commons/Splitter.control */
/* =============================================== */
/*
.sapUiSplitterFocus,
.sapUiVSplitterSecondPane:focus,
.sapUiHSplitterSecondPane:focus,
.sapUiVSplitterFirstPane:focus,
.sapUiHSplitterFirstPane:focus {
	outline: @sapUiBlackBorder dotted 1px;
}
*/

.sapUiHorizontalSplitterBar:focus,
.sapUiVerticalSplitterBar:focus {
	outline: @sapUiHighlight dashed 1px;
}

.sapUiVSplitterFirstPane {
	height: 100%;
	display: inline-block;
	vertical-align: top;
	outline-offset: -1px;
	/*overflow: hidden;*/
}

.sapUiHSplitterFirstPane {
	width: 100%;
	vertical-align: top;
	outline-offset: -1px;
	/*overflow: hidden;*/
}

.sapUiVSplitterSecondPane {
	height: 100%;
	display: inline-block;
	vertical-align: top;
	outline-offset: -1px;
	/*overflow: hidden;*/
}

.sapUiHSplitterSecondPane {
	width: 100%;
	vertical-align: top;
	outline-offset: -1px;
	/*overflow: hidden;*/
}

.sapUiVerticalSplitterBar {
	background-image: url(../base/img/splitter/splitter_vert_grip.gif);
	background-color: darken(@sapUiBaseBG, 20);
	background-position: center center;
	background-repeat: no-repeat;
	height: 100%;
	display: inline-block;
	vertical-align: top; /* needed to hide vertical scrollbar when all elements fit inside the pane. currently works only in FF*/
	/*z-index:1;*/
}

.sapUiVerticalSplitterBarHidden {
	display: none;
	vertical-align: top; /* needed to hide vertical scrollbar when all elements fit inside the pane. currently works only in FF*/
	height: 100%;
	/*z-index:1;*/
}

.sapUiHorizontalSplitterBar {
	background-image: url(../base/img/splitter/splitter_horiz_grip.gif);
	background-position: center center;
	background-repeat: no-repeat;
	background-color: darken(@sapUiBaseBG, 20);
	width:100%;
	/*z-index:1;*/
}

.sapUiHorizontalSplitterBarHidden {
	display: none;
	width:100%;
	/*z-index:1;*/
}

.sapUiVerticalSplitterBar:hover {
	background-color: darken(@sapUiBaseBG, 40);
	cursor: col-resize;
}

.sapUiHorizontalSplitterBar:hover {
	background-color: darken(@sapUiBaseBG, 40);
	cursor: row-resize;
}

.sapUiVSBGhost {
	background-image: url(../base/img/splitter/checkerboard.png);
	background-repeat: repeat;
	position: absolute;
	cursor:   col-resize;
	z-index: 20;
}

.sapUiHSBGhost {
	background-image: url(../base/img/splitter/checkerboard.png);
	background-repeat: repeat;
	position: absolute;
	cursor:  row-resize;
	z-index: 20;
}
/* END "Splitter.less" */

/* START "TabStrip.less" */
.sapUiTabStrip {
	border: solid 1px @sapUiMediumBorder;
	font-family: @sapUiFontFamily;
	font-size: @sapUiFontSize;
	box-sizing: border-box;
	position:relative;
}

.sapUiTabBar {
	position: relative;
	white-space: nowrap;
	cursor: default;
}

.sapUiTabBarCnt {
	white-space: nowrap;
	display: inline-block;
	overflow:hidden;
	margin-top: 0;
	padding-left: 0;
	outline: none;
}

.sapUiTab,
.sapUiTabSel,
.sapUiTabDsbl {
	position: relative;
	display: inline-block;
	vertical-align: top;
	white-space:nowrap;
}

.sapUiTabL {
	overflow: hidden;
}

.sapUiTabDsbl {
	color: @sapUiContentDisabledTextColor;
}

.sapUiTabDsbl .sapUiTabIco {  /* make icon on disabled tab look faded out */
	opacity: .3;
	filter: alpha(opacity=30);
}

.sapUiTabClose {
	vertical-align:top;
	border: none;
	background-color: transparent;
	cursor: pointer;
}

.sapUiTabIco {
	width:16px; /* TODO: application contract... */
	height:16px;
	vertical-align:top;
}

span.sapUiTabIco {
	font-size: 16px;
}

.sapUiTabBar .sapUiTabMenu {
	display: inline-block;
	float: right;
	display:none; /* will be toggled by framework code */
}

.sapUiTabPanel {
	overflow: auto;
	left:0;
	right:0;
}

/* If height is defined it must be positioned absolute to fit the height*/
.sapUiTabPanelHeight {
	position: absolute;
	bottom:0;
}

.sapUiTabStripNoSelection {
	-moz-user-select: none;
	-webkit-user-select: none;
	user-select: none;
}

.sapUiTabBar .sapUiTabStripScrollContainer {
	display: inline-block;
	position: absolute;
	overflow: hidden;

	left: 0;
	right: 0;
}

.sapUiTabBar .sapUiTabStripScrollIcon {
	width: 16px;
	height: 22px;
	font-size: 10px;
	margin-top: 6px;
	padding-top: 4px;
	vertical-align: top;
	visibility: hidden;
	display: none;

	box-sizing: border-box;

	outline: none;
	-webkit-user-select: none;  /* Chrome all / Safari all */
	-moz-user-select: none;     /* Firefox all */
	user-select: none;
}

.sapUiTabBar .sapUiTabStripRightScrollIcon {
	position: absolute;
	right: 0;
}

.sapUiTabStrip {
	&.sapUiTabStripScrollForward .sapUiTabStripRightScrollIcon,
	&.sapUiTabStripScrollBack .sapUiTabStripLeftScrollIcon {
		visibility: visible;
	}
}

.sapUiTabStrip.sapUiTabStripScrollable .sapUiTabBar {
	.sapUiTabStripScrollIcon {
		display: inline-block;
	}

	.sapUiTabStripScrollContainer {
		left: 16px;
		right: 16px;
	}
}

/* END "TabStrip.less" */

/* START "TextArea.less" */
/* =============================================== */
/* CSS for control sap.ui.commons/TextArea.control */
/* =============================================== */

.sapUiTxtA{
	resize: none; /* disable safari resize */
}

.sapUiTf.sapUiTxtA:not(.sapUiTfInner){
	height: auto; /* to overwrite defaults from TextField */
	width: auto;
	line-height: normal;
}
/* END "TextArea.less" */

/* START "TextField.less" */
/* ================================================ */
/* CSS for control sap.ui.commons/TextField.control */
/* ================================================ */

@_sap_ui_commons_TextField_Height: 16px;

.sapUiTf{
	box-sizing: border-box;
	vertical-align: top;
	font-family: @sapUiFontFamily;
	font-size: @sapUiFontSize;
	-webkit-appearance: none; /*Needed for safari mobile*/
}

.sapUiTf::-ms-clear{
	width : 0;
	height: 0;
	display:none;
}

.sapUiTf:not(.sapUiTfInner){
	width: 22ex;
}

/* Inner (ComboBox) */
.sapUiTfInner.sapUiTf{
	height: 100%;
	background: transparent;
	color: inherit; /* not automatic inherited from outer DIV */
	outline: none; /* disable safari outline */
}

html[data-sap-ui-os^='mac'] .sapUiTfDsbl .sapUiTfInner.sapUiTf{
	color: darken(@sapUiContentDisabledTextColor, 25); /* color for disabled inputs is lighter by browser */
}

.sapUiTfBrd{
	border: 1px solid @sapUiFieldBorderColor;
	-webkit-border-radius: 0px; /*Needed for safari mobile*/
}

.sapUiTf:not(.sapUiTfBrd){
	border: none;
}

.sapUiTfMono{
	font-family: @sapUiFontMonospaceFamily;
}

.sapUiTfStd{
	background-color: @sapUiFieldBackground;
	color: @sapUiBaseText;
}

.sapUiTfRo{
	background-color: @sapUiFieldReadOnlyBackground;
	color: @sapUiBaseText;
}

.sapUiTfRo > .sapUiTfInner{
	cursor: default; /* in ComboBox Cursor is overwitten */
}

.sapUiTfFoc{
	background-color: @sapUiFieldBackground;
	outline: none; /* hide e.g. chrome focus outline */
}

.sapUiTfBrd.sapUiTfFoc{
	border-color: @sapUiFieldFocusBorderColor;
}

.sapUiTfBrd.sapUiTfErr{
	border-color: @sapUiFieldInvalidColor;
}

.sapUiTfBrd.sapUiTfWarn {
	border-color: @sapUiFieldWarningColor;
}

.sapUiTfBrd.sapUiTfSucc {
	border-color: @sapUiFieldSuccessColor;
}

.sapUiTfErr.sapUiTfStd{
	background-color: @sapUiFieldInvalidBackground;
}

.sapUiTfWarn.sapUiTfStd{
	background-color: @sapUiFieldWarningBackground;
}

.sapUiTfSucc.sapUiTfStd{
	background-color: @sapUiFieldSuccessBackground;
}

.sapUiTfDsbl{
	background-color: @sapUiFieldReadOnlyBackground;
	color: @sapUiContentDisabledTextColor;
	/* Avoid text selection in disabled TextFields */
	-moz-user-select: none;
	-webkit-user-select: none;
	user-select: none;
}

.sapUiTfBrd.sapUiTfDsbl{
	border-color: @sapUiFieldDisabledBorderColor;
}

/* need to be separate statements for each browser, otherwise it will not work */
.sapUiTfPlace{
	color: @sapUiExtraLightText;
}
.sapUiTf::-webkit-input-placeholder{
	color: @sapUiExtraLightText;
}
.sapUiTf::-moz-placeholder{
	color: @sapUiExtraLightText;
}
.sapUiTf:-ms-input-placeholder{
	color: @sapUiExtraLightText;
}

/* END "TextField.less" */

/* START "TextView.less" */
.sapUiTv {
	font-family: @sapUiFontFamily;
	font-size: @sapUiFontSize;
	vertical-align: top;
	line-height: @sapUiLineHeight;
	display: inline-block;
}

.sapUiInverted-CTX .sapUiTv {
	color: @sapUiTextInverted;
}

/* Do not invert for table */
.sapUiInverted-CTX .sapUiTable .sapUiTv {
	color: @sapUiBaseText;
}

.sapUiTvH1 { /* be careful - used in other controls with title too */
	font-size: @sapUiFontHeader1Size;
	font-weight: bold;
	line-height: 122%;
	color: @sapUiTextTitle;
}

.sapUiTvH2 {
	font-size: @sapUiFontHeader2Size;
	font-weight: bold;
	line-height: 122%;
	color: @sapUiTextTitle;
}

.sapUiTvH3 {
	font-size: @sapUiFontHeader3Size;
	font-weight: bold;
	line-height: 122%;
	color: @sapUiTextTitle;
}

.sapUiTvH4 {
	font-size: @sapUiFontHeader4Size;
	font-weight: bold;
	color: @sapUiTextTitle;
}

.sapUiTvH5 {
	font-size: @sapUiFontHeader5Size;
	font-weight: bold;
	color: @sapUiTextTitle;
}

.sapUiTvH6 {
	font-size: @sapUiFontHeader6Size;
	font-weight: bold;
	color: @sapUiTextTitle;
}

.sapUiTvItalic {
	font-style: italic;
}

.sapUiTvSmall {
	font-size: @sapUiFontSmallSize;
}

.sapUiTvMono {
	font-family: monospace;
}

.sapUiTvULine {
	text-decoration: underline;
}

/* disabled */
.sapUiTvDsbl {
	cursor: default;
	-moz-user-select: none;
	color: @sapUiContentDisabledTextColor;
	outline: none;
}

.sapUiInverted-CTX .sapUiTv.sapUiTvDsbl {
	color: @sapUiContentDisabledTextColor;
}

/* success state */
.sapUiTvSucc {
	background-color: @sapUiFieldSuccessBackground;
}

.sapUiInverted-CTX .sapUiTvSucc {
	color: @sapUiBaseText;
}

/* Do not invert for table */
.sapUiInverted-CTX .sapUiTable .sapUiTvSucc {
	color: @sapUiFieldSuccessBackground;
}

/* invalid state */
.sapUiTvErr {
	color: @sapUiFieldInvalidColor;
	border-color: @sapUiFieldInvalidColor;
	background-color: @sapUiFieldInvalidBackground;
}

.sapUiInverted-CTX .sapUiTvErr {
	color: @sapUiFieldInvalidColor;
}

/* warning state */
.sapUiTvWarn {
	background-color: @sapUiFieldWarningBackground;
}

.sapUiInverted-CTX .sapUiTvWarn {
	color: @sapUiBaseText;
}

/* Do not invert for table */
.sapUiInverted-CTX .sapUiTable .sapUiTvWarn {
	color: @sapUiBaseText;
}

/* Designs */
.sapUiTvEmph {
	font-weight: bold;
}

/* alignments */
.sapUiTvAlignRight{
	text-align: right;
}

.sapUiTvAlignLeft{
	text-align: left;
}

.sapUiTvAlignCenter{
	text-align: center;
}

.sapUiTvInfo{
	position: absolute;
	left: 0;
	top: 0;
	background-color: @sapBackgroundColor;
}

.sapUiTvWrap{
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}
/* END "TextView.less" */

/* START "ToggleButton.less" */
/* =================================================== */
/* CSS for control sap.ui.commons/ToggleButton.control */
/* =================================================== */

.sapUiBtnS.sapUiBtnNorm.sapUiBtnStd.sapUiToggleBtn.sapUiToggleBtnPressed {
	color: @sapUiTextSelection;
	background-color: @sapUiToggleButtonPressedBackground;
}

.sapUiBtnS.sapUiBtnDsbl.sapUiToggleBtn.sapUiToggleBtnPressed {
	background-color: @sapUiToggleButtonPressedDisabledBackground;
}
/* END "ToggleButton.less" */

/* START "Toolbar.less" */
/**
 * All Toolbar items are arranged in a flow layout. Once there are too many items to display, some flow into the second row
 * which is not visible.
 */

.sapUiTb {
	height: 26px;
	border: none;
	padding: 0;
	margin: 0;
	overflow: hidden;
	position: relative;
	white-space: normal; // make sure parents don't break the toolbar logic for overflowing to the second line
}

.sapUiTb.sapUiTbStandalone {
	height: 28px; /* standalone case has borders one layer below, so the total height of the outermost element needs to be increased */
}

.sapUiTbDesignTransparent,
.sapUiTbDesignFlat {
	background-color: transparent;
}

.sapUiTbDesignStandard {
	background-color: @sapUiDarkBG;
}

/* ToolBar items CONTainer (the outer one, sapUiTbInner is the inner one...)
   This one is required for the borders, as setting them outside would e.g. invalidate width settings. */
.sapUiTbCont {
	height: 22px;
	line-height: 22px;
	overflow: hidden;   /* hide the items flowing into the second row */
	position: relative; /* required for IE7 to make overflow:hidden work! */
	padding: 4px 0px 0px 10px;
}

.sapUiTbContLeft.sapUiTbCont{
	float: left;
	width: 100%;
	box-sizing: border-box;
	height: 27px;
	padding-top: 1px;
}

.sapUiTbStandalone .sapUiTbCont {
	border-width: 1px;
	border-style: solid;
}

/* inner container holding all the Toolbar contents */
.sapUiTbInner {
	position: relative; /* to allow positioning the overflow button and the cover */
	overflow: visible;  /* required for the overflow button which is positioned outside! The real overflow hiding is done one level above */
	margin-right: 20px; /* the space for the overflow button */
}

/* regular padding between Toolbar items */
.sapUiTbInner > * , .sapUiTbInnerRight > * {
	margin-right: 2px;
	margin-bottom: 20px; /* make sure the second row is out of sight */
}

.sapUiTbInnerRight {
	text-align: right;
	position: absolute;
	right: 0px;
	padding: 2px 0px 0px 10px;
	height: 22px;
	white-space: nowrap;
}

/* ToolBar Overflow Button ("chevron arrows") */
.sapUiTbOB {
	background-image: url(../base/img/toolbar/overflow.png);
	background-position: center center;
	background-repeat: no-repeat;
	display: none;  /* will be modified by Toolbar.js for displaying/hiding on resize */
	width: 14px;
	height: 20px;
	position: absolute;
	right: -14px; /* position the overflow button right outside of its container */
	top: 0px;
	cursor: pointer;
	margin-right: 0; /* remove that standard Toolbar item margin */
}

/* large space between Toolbar items */
.sapUiTbSpacer {
	display: inline-block;
	width: 14px;
	height: 2px;
}

/* large space with separator line between Toolbar items */
.sapUiTbSeparator {
	display: inline-block;
	width: 14px;
	/* Concrete themes must provide the separator graphics */
}

/* ToolBar overflow Drop-Down menu */
.sapUiTbDD {
	position: absolute;
	top: 0; /* is only made visible after moved to the right position; not setting top:0 causes flashing scrollbars */
	display: none;
	width: 222px;
}

.sapUiTbDD > div {
	border-width: 1px;
	border-style: solid;
	border-color: @sapUiMediumBorder;
	padding: 6px;
}

.sapUiTbDD > div > * {
	margin: 2px;
}

/* disabled buttons are focuses in Toolbar by ItemNavigation -> so make visible using simple outline*/
.sapUiTbInner > .sapUiBtnDsbl:focus,
.sapUiTbInnerRight > .sapUiBtnDsbl:focus{
	outline: 1px dotted @sapUiBaseBorder;
}

/* Toolbar inside of Form as Form- and Container- header */
.sapUiGridTitle > th > .sapUiTb,
.sapUiFormToolbar > .sapUiTb{
	border-bottom: 2px solid @sapUiMediumBorder;
}

/* END "Toolbar.less" */

