	line-height: 18px;
	white-space: nowrap;
	margin-bottom: 3px;
	border: 1px solid transparent;
	border-radius: 1px;
	-moz-user-select: none;
}

.sapUiTreeContScroll .sapUiTreeNode {
	float: left;
	clear: left;
	width: 100%;
