/* START "PDFViewport.less" */
.sapVizKitPDFViewport {
	.sapUiVizkitRedlineTool {
		z-index: 1;
	}

	// The TransformSvgElement gizmo surface is positioned above the hotspot SVG surface which has
	// z-index 1.
	.sapUiVkTransformationSvgElementToolSurface {
		z-index: 2;
	}
}

.sapVizKitPDFScrollContainer {
	// To make the PDF high-res canvas be positioned in the scroll container coordinate space.
	position: relative;

	// To make the scroll container fill the entire viewport.
	width: 100%;
	height: 100%;

	display: flex;
	// Pages are stacked vertically.
	flex-direction: column;

	box-sizing: border-box;
	padding: 10px;

	.sapVizKitPDFDocument {
		display: flex;
		// Pages are stacked vertically.
		flex-direction: column;

		// To center the document as flex item in the viewport.
		margin: auto;

		.sapVizKitPDFPage {
			box-shadow: 0 2px 8px 0 fade(@sapUiContentShadowColor, 30);

			// The page's position is relative so that the high-res canvas can be positioned
			// absolutely in its space.
			position: relative;

			.sapVizKitPDFNormalCanvas {
				position: relative;
			}

			.sapVizKitPDFStretchedCanvas {
				position: relative;
			}

			.sapVizKitPDFHighResCanvas {
				position: absolute;
				touch-action: none;
				pointer-events: none;
			}

			.sapVizKitPDFHotspotSurface {
				position: absolute;
				z-index: 1;
				width: 100%;
				height: 100%;
				pointer-events: none;

				rect, ellipse, path {
					pointer-events: auto;
				}

				.sapUiVizKitNonInteractiveHotspot {
					rect, ellipse, path {
						pointer-events: none;
					}
				}
			}

			canvas {
				background-color: white;

				// Override the style defined in Viewport.less.
				outline-style: none;
			}
