// PageGallery
//   PageGallerySlider
//   PageGalleryPages
//     PageGalleryItem
//       img
//       label
//     PageGalleryItem
//       img
//       label
//     ...
//
// NOTE: We use the negative `outline-offset` so that when we call `element.scrollIntoView()` the
// outline is not clipped by the viewport.

.sapVizKitPageGallery {
    display: flex;
    flex-direction: column;
    min-width: 320px;
}

.sapVizKitPageGalleryDisabled {
    opacity: @sapUiContentDisabledOpacity;
    pointer-events: none;
    user-select: none;
}

.sapVizKitPageGallerySlider {
    margin: 0 @sapUiMarginSmall;
}

.sapVizKitPageGalleryPages {
    display: flex;
    flex-direction: column;
    row-gap: @sapUiMarginSmall;
    padding: @sapUiMarginSmall;
    height: 100%;
    overflow: auto;
}

.sapVizKitPageGalleryItem {
    display: flex;
    flex-direction: column;
    align-items: center;
    row-gap: @sapUiMarginTiny;
    // NOTE: For `margin: auto` in flexbox with overflow see
    // https://stackoverflow.com/questions/32528425/centering-items-with-flexbox-and-overflow.
    margin: 0 auto;

    img {
        box-shadow: 0 2px 4px 0 fade(@sapUiContentShadowColor, 20);

        &:focus:not(.sapVizKitPageGalleryItemSelected) {
            outline: @sapUiContentFocusWidth dotted black;
            outline-offset: -@sapUiContentFocusWidth;
        }

        &:hover:not(.sapVizKitPageGalleryItemSelected) {
            outline: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiButtonInformationHoverBorderColor;
            outline-offset: -@sapUiContentFocusWidth;
        }
    }
}

.sapVizKitPageGalleryItemSelected {
    outline: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiSelected;
    outline-offset: -@sapUiContentFocusWidth;
}

.sapVizKitPageGalleryToolbarCurrentPageIndex {
    .sapMInputBaseInner {
        text-align: center;
        padding: 0;
    }
}

.sapVizKitPageGalleryToolbarSlash {
    flex-shrink: 0;
    margin: 0 @sapUiMarginTiny;
}

/* END "PageGallery.less" */

