}

.sapUiSizeCozy #allMyAppsView--sapUshellDetailsPageHeaderBar {
  height: 3rem;
}

#allMyAppsView--sapUshellAllMyAppsDetailsHeaderLabel {
  padding-bottom: 0.5rem;
  padding-left: 0.75rem;
  max-width: ~'calc(100% - 1.5rem)';
