    text-shadow: @sapUiShellCategory3TextShadow;
  }
}

.sapUshellSysInfoBarRed {
  background-color: @sapUiShellCategory11Background;
  border-color: @sapUiShellCategory11BorderColor;

  .sapUshellSysInfoBarText, .sapMLabelTextWrapper {
    color: @sapUiShellCategory11TextColor;
    text-shadow: @sapUiShellCategory11TextShadow;
  }
}

.sapUshellSysInfoBarPink {
  background-color: @sapUiShellCategory13Background;
  border-color: @sapUiShellCategory13BorderColor;

  .sapUshellSysInfoBarText, .sapMLabelTextWrapper {
    color: @sapUiShellCategory13TextColor;
    text-shadow: @sapUiShellCategory13TextShadow;
  }
}

.sapUshellSysInfoBarViolet {
  background-color: @sapUiShellCategory12Background;
  border-color: @sapUiShellCategory12BorderColor;

  .sapUshellSysInfoBarText, .sapMLabelTextWrapper {
    color: @sapUiShellCategory12TextColor;
    text-shadow: @sapUiShellCategory12TextShadow;
  }
}

.sapUshellSysInfoBarBlue {
  background-color: @sapUiShellCategory9Background;
  border-color: @sapUiShellCategory9BorderColor;

  .sapUshellSysInfoBarText, .sapMLabelTextWrapper {
    color: @sapUiShellCategory9TextColor;
    text-shadow: @sapUiShellCategory9TextShadow;
  }
}

.sapUshellSysInfoBarGreen {
  background-color: @sapUiShellCategory16Background;
  border-color: @sapUiShellCategory16BorderColor;

  .sapUshellSysInfoBarText, .sapMLabelTextWrapper {
    color: @sapUiShellCategory16TextColor;
    text-shadow: @sapUiShellCategory16TextShadow;
  }
}

.sapUshellSysInfoBarGrey {
  background-color: @sapUiShellCategory15Background;
  border-color: @sapUiShellCategory15BorderColor;

  .sapUshellSysInfoBarText, .sapMLabelTextWrapper {
    color: @sapUiShellCategory15TextColor;
    text-shadow: @sapUiShellCategory15TextShadow;
  }
}

/* END "SysInfoBar.less" */

/* START "TargetMappingConfiguration.less" */
/* ============================================= */
/* CSS for Target Mapping                        */
/* ushell/components/tiles/action/Configuration  */
/* ============================================= */

.sapUshellTargetMappingToUpperCase > .sapMInputBaseInner {
    text-transform: uppercase;
}

/* END "TargetMappingConfiguration.less" */

/* START "Tile.less" */
/* ===================================================== */
/* CSS for control "sap.ushell.ui.launchpad.Tile" (base) */
/* ===================================================== */

@_sap_ushell_Tile_WidthDesktop: 11rem;
@_sap_ushell_Tile_HeightDesktop: 11rem;
@_sap_ushell_Tile_LongWidthDesktop: (2 * @_sap_ushell_Tile_WidthDesktop) + @_sap_ushell_Tile_Spacing;

@_sap_ushell_Tile_WidthPhone: 9.25rem;
@_sap_ushell_Tile_HeightPhone: 9.25rem;
@_sap_ushell_Tile_LongWidthPhone: (2 * @_sap_ushell_Tile_WidthPhone) + @_sap_ushell_Tile_Spacing;

@_sap_ushell_Tile_ShadowWidth: 0.25rem;

/* BCP 1870411969: keep in mind two standard Tiles should fit in the same row on 320px width; see also "(min-width: 375px)" */
@_sap_ushell_Tile_SpacingXS: 0.475rem;
@_sap_ushell_Tile_SpacingS: 0.475rem;
@_sap_ushell_Tile_Spacing: 0.5rem;
@_sap_ushell_Tile_WidthXS: 4.375rem;
@_sap_ushell_Tile_WidthS: 5.25rem;
@_sap_ushell_Tile_Width: 5.25rem;

/* Tile footer (plus button in tile catalog) */
@_sap_ushell_Tile_FooterCornerRadius: 0.25rem;
@_sap_ushell_Tile_FooterPosition: relative;
@_sap_ushell_Tile_FooterPositionTop: 0.44rem;
@_sap_ushell_Tile_FooterTextAlignment: center;
@_sap_ushell_Tile_FooterLineHeight: inherit;

.sapUshellTile:hover .sapUshellTileFooterElement {
  z-index: auto;
}

#catalogTiles .sapUshellTile {
  overflow: visible;
}

#catalogTiles.sapUshellTileContainer .sapUshellTileInner {
  position: relative;
}

.sapUshellTileEntrance {
  -webkit-animation: sapUshellTileEntranceAnimation 0.3s;
  animation: sapUshellTileEntranceAnimation 0.3s;
}

.sapUshellTileActionDivCenter {
  border-radius: @sapUiTileBorderCornerRadius;
}

@-webkit-keyframes sapUshellTileEntranceAnimation {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

@keyframes sapUshellTileEntranceAnimation {
  0% { opacity: 0; }
  100% { opacity: 1; }
}

.sapUshellTile,
.sapUshellTileInner {
  -webkit-user-select: none;
  -moz-user-select: none;
  user-select: none;
  -webkit-touch-callout: none;
}

.sapUshellTile {
  float: left;
  cursor: pointer;
  position: relative;
  margin: 0 @_sap_ushell_Tile_Spacing @_sap_ushell_Tile_Spacing 0;

  .sap-phone & {
    margin: 0; /* flex is used on phones, see "TileContainer.less" (base) */
  }

  &.sapUshellHidden,
  .sapUshellTileActionsIconClass {
    display: none;
  }

  .sapUshellTileInner {
    display: block;
  }

  /* no link decorations for inner controls */
  a.sapUshellTileInner {
    text-decoration: none;
  }
}

.sapUshellCatalogsContainer .sapUshellTile:not(.sapUshellTileWrapper) {
  /* allows real custom Tiles (i.e. not "sapUshellAppBox") to be taller in the AppFinder (i.e. due to "sapUshellTilePinButtonOverlay") */
  height: revert;
}

/* DOM hierarchy: .sapUshellTile[ > ].sapUshellTileWrapper > sapUshellTileInner > sapUshellTileBase */
.sapUshellTile,
.sapUshellTileWrapper,
.sapUshellTileInner,
.sapUshellTileBase,
.sapUshellInner .sapUshellTile-placeholder,
.sapUshellInner .sapUshellLinkTile-placeholder {
  outline: none;
  box-sizing: border-box;
  border-radius: @sapUiTileBorderCornerRadius;
  width: @_sap_ushell_Tile_WidthPhone;
  height: @_sap_ushell_Tile_HeightPhone;

  &.sapUshellLong,
  .sapUshellLong & {
    width: @_sap_ushell_Tile_LongWidthPhone;
    height: @_sap_ushell_Tile_HeightPhone;
  }

  @media (min-width: 375px) {
    width: @_sap_ushell_Tile_WidthDesktop;
    height: @_sap_ushell_Tile_HeightDesktop;

    &.sapUshellSmall,
    .sapUshellSmall & {
      width: @_sap_ushell_Tile_WidthPhone;
      height: @_sap_ushell_Tile_HeightPhone;
    }

    &.sapUshellLong,
    .sapUshellLong & {
      width: @_sap_ushell_Tile_LongWidthDesktop;
      height: @_sap_ushell_Tile_HeightDesktop;

      &.sapUshellSmall,
      .sapUshellSmall & {
        width: @_sap_ushell_Tile_LongWidthPhone;
        height: @_sap_ushell_Tile_HeightPhone;
      }
    }
  }
}

#shellPage .sapUshellLinkTile-clone {
  vertical-align: middle;
  line-height: normal !important;
  background-color: lighten(@sapBrandColor, 48) !important;
  height: initial;
  padding: 0;

  br,
  .sapMGTLineStyleHelper,
  .sapMGTActionsContainer {
    display: none;
  }
}

#shellPage .sapUshellTile-clone {
  box-shadow: @sapUiShadowLevel2;

  .sapUshellTileActionIconDivBottom,
  .sapUshellTileDeleteClickArea {
    display: none;
  }
}

.sap-desktop {
  .sapUshellTileContainerLocked,
  .sapUshellTileContainerEditMode {
    .sapUshellTile .sapUshellTileActionsIconClass {
      display: none;
    }
  }

  .sapUshellTile {
    &.showTileActionsIcon,
    &:hover {
      .sapUshellTileActionsIconClass.sapUiIcon {
        opacity: 1;
        transition: opacity 0.3s ease-in-out;
      }
    }

    .sapUshellTileActionsIconClass.sapUiIcon {
      position: relative;
      float: right;
      display: block;
      cursor: pointer;
      height: 2rem;
      width: 2rem;
      border-bottom-right-radius: 0.25rem;
      z-index: 1;
      transform: rotate(90deg);
      -ms-transform: rotate(90deg);
      -webkit-transform: rotate(90deg);
      transition: opacity 0.3s ease-in-out;
      opacity: 0;
      margin-top: -@_sap_ushell_Tile_HeightDesktop;
    }
  }
}

.sapUshellTile:focus::after {
  content: "";
  position: absolute;
  top: 0.1875rem;
  bottom: 0.1875rem;
  left: 0.1875rem;
  right: 0.1875rem;
  z-index: 2;
  pointer-events: none;
  border-radius: @sapUiTileBorderCornerRadius;
}

.sapUshellTile .sapUiIcon {
  cursor: pointer;
}

#catalogTiles .sapUshellTile:focus .sapMGT {
  border-color: transparent;
}

/* END "Tile.less" */

/* START "TileBase.less" */
/* ==================================================== */
/* CSS for control "sap.ushell.ui.tile.TileBase" (base) */
/* ==================================================== */

@_sap_ushell_TileBase_TextHighlightBgColor: @sapUiWarningBG; /* highlights for found search terms */

@_sap_ushell_TileBase_TitleFontFamily: @sapUiFontFamily;
@_sap_ushell_TileBase_FontWeight: normal;

@_sap_ushell_TileBase_SubtitleFontFamily: @sapUiFontFamily;
@_sap_ushell_TileBase_SubtitleFontWeight: normal;

@_sap_ushell_TileBase_TitleFontSizeDesktop: 1rem;
@_sap_ushell_TileBase_TitleLineHeightDesktop: 1.25rem;
@_sap_ushell_TileBase_TitleMaxHeightDesktop: 2.4rem; /* fix ie bug - cut line */

@_sap_ushell_TileBase_TitleFontSizePhone: 0.875rem;
@_sap_ushell_TileBase_TitleLineHeightPhone: 1.25rem;

@_sap_ushell_TileBase_SubtitleFontSizeDesktop: 0.875rem;
@_sap_ushell_TileBase_SubtitleLineHeightDesktop: 1.375rem;

@_sap_ushell_TileBase_SubtitleFontSizePhone: 0.75rem;
@_sap_ushell_TileBase_SubtitleLineHeightPhone: 1.125rem;

@_sap_ushell_TileBase_BottomLineFontFamily: @sapUiFontFamily;
@_sap_ushell_TileBase_BottomLineFontWeight: normal;

@_sap_ushell_TileBase_BottomLineFontSizeDesktop: 0.875rem;
@_sap_ushell_TileBase_BottomLineLineHeightDesktop: 1.38rem;
@_sap_ushell_TileBase_BottomLineBottomDesktop: 0.125rem;

@_sap_ushell_TileBase_BottomLineFontSizePhone: 0.75rem;
@_sap_ushell_TileBase_BottomLineLineHeightPhone: 1.13rem;
@_sap_ushell_TileBase_BottomLineBottomPhone: 0.31rem;

@_sap_ushell_TileBase_IconBottomDesktop: 2rem;
@_sap_ushell_TileBase_IconFontSizeDesktop: 2.25rem;

@_sap_ushell_TileBase_IconBottomPhone: 1.81rem;
@_sap_ushell_TileBase_IconFontSizePhone: 1.5rem;

@_sap_ushell_TileBase_PaddingDesktopHorizontal: 0.5rem;
@_sap_ushell_TileBase_PaddingDesktopVertical: 0.5rem;

@_sap_ushell_TileBase_PaddingPhoneHorizontal: 0.375rem;
@_sap_ushell_TileBase_PaddingPhoneVertical: 0.625rem;

.sapUshellTileBase {
  border: 1px solid @sapUiTileBorderColor;
  background: @sapUiTileBackground;
  box-shadow: @sapUiShadowLevel0;
  height: 100%;
  width: 100%;
  position: relative;
  box-sizing: border-box;

  .sapUshellTileBaseHeader {
    pointer-events: none;
  }

  .sapUshellTileBaseTitle,
  .sapUshellTileBaseSubtitle {
    padding: 0;
    margin: 0;
    text-overflow: ellipsis;
    -o-text-overflow: ellipsis;
    overflow: hidden;
  }

  .sapUshellTileBaseTitle {
    word-wrap: break-word;
  }

  .sapUshellTileBaseSubtitle {
    white-space: nowrap;
    word-wrap: normal;
  }

  .sapUshellTileBaseIcon {
    pointer-events: none;
    position: absolute;
    left: auto !important;
    cursor: pointer;
  }

  .sapUshellTileBaseInfo {
    text-overflow: ellipsis;
    -o-text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
    bottom: 0.25rem;
    width: 94%;
    position: absolute;
    pointer-events: none;
  }
}

.sapUiMedia-Std-Phone .sapUshellTileBase {
  margin: 0;
  padding: @_sap_ushell_TileBase_PaddingPhoneHorizontal @_sap_ushell_TileBase_PaddingPhoneVertical;

  .sapUshellTileBaseTitle {
    line-height: @_sap_ushell_TileBase_TitleLineHeightPhone;
    max-height: @_sap_ushell_TileBase_TitleLineHeightPhone * 2;
  }

  .sapUshellTileBaseSubtitle {
    line-height: @_sap_ushell_TileBase_SubtitleLineHeightPhone;
  }

  .sapUshellTileBaseIcon {
    bottom: @_sap_ushell_TileBase_IconBottomPhone;
  }

  .sapUshellTileBaseInfo {
    bottom: @_sap_ushell_TileBase_BottomLineBottomPhone;
    line-height: @_sap_ushell_TileBase_BottomLineLineHeightPhone;
    text-align: right;
    right: @_sap_ushell_TileBase_PaddingPhoneHorizontal;
  }
}

.sapUiMedia-Std-Tablet,
.sapUiMedia-Std-Desktop {
  .sapUshellTileBase {
    margin: 0;
    padding: @_sap_ushell_TileBase_PaddingDesktopHorizontal @_sap_ushell_TileBase_PaddingDesktopVertical;

    .sapUshellTileBaseTitle {
      line-height: 1.15;
      max-height: @_sap_ushell_TileBase_TitleMaxHeightDesktop;
      padding-bottom: 0;
      overflow: hidden;
      text-overflow: ellipsis;
      display: -webkit-box;
      -webkit-line-clamp: 2;
      -webkit-box-orient: vertical;
    }

    .sapUshellTileBaseIcon {
      bottom: @_sap_ushell_TileBase_IconBottomDesktop;
    }

    .sapUshellTileBaseInfo {
      bottom: @_sap_ushell_TileBase_BottomLineBottomDesktop;
      line-height: @_sap_ushell_TileBase_BottomLineLineHeightDesktop;
      text-align: right;
      right: @_sap_ushell_TileBase_PaddingDesktopHorizontal
    }
  }
}

/* END "TileBase.less" */

/* START "TileContainer.less" */
/* ============================================================== */
/* CSS for control "sap.ushell.ui.launchpad.TileContainer" (base) */
/* ============================================================== */

/* Circles for dragging indicator */
.sapUshellCircleBase {
  height: 0.5rem;
  width: 2.5rem;
  margin-left: auto;
  margin-right: auto;
}

.sapUshellCircle {
  width: 0.25rem;
  height: 0.25rem;
  display: inline-block;
  margin-left: 0.125rem;
  vertical-align: top;
}

/* all TileContainers */
.sapUshellDisableLockedGroupDuringDrag .sapUshellInner,
.sapUshellDisableLockedGroupDuringDrag .sapUshellLineModeContainer {
  filter: grayscale(100%);
  -webkit-filter: grayscale(100%);
  opacity: 0.5;
  position: relative; /* needed because of bug in IE */
}

.sapUshellInner {
  display: block;
  list-style-type: none;
  padding: 0;
  margin: 0;

  .sap-phone & {
    display: flex;
    flex-wrap: wrap;
    gap: @_sap_ushell_Tile_Spacing;
  }
}

.sapUshellTileContainerContent .sapUshellTileContainerHeader.sapUshellGroupHeaderHidden {
  height: 0;
  display: none;
}

.sapUshellTileContainerContent .sapUshellTileContainerHeader.sapUshellFirstGroupHeaderHidden {
  height: 0;
}

.sapUshellTileContainer {
  .sapFCard {
    margin: 0 @_sap_ushell_Tile_Spacing @_sap_ushell_Tile_Spacing 0;

    .sap-phone & {
      margin: 0; /* flex is used on phones */
    }

    &:focus::after {
      content: "";
      position: absolute;
      top: 0.1875rem;
      bottom: 0.1875rem;
      left: 0.1875rem;
      right: 0.1875rem;
      z-index: 2;
      pointer-events: none;
      border-radius: 0.25rem;
    }
  }

  &.sapUshellHidden {
    display: none;
  }

  .sapUshellTileContainerBeforeContent,
  .sapUshellTileContainerAfterContent {
    max-width: 10rem;
    margin-left: auto;
    margin-right: auto;
    text-align: center;

    /* in order to add 0.5 rem to each inner side of the Add Button */
    .sapUshellAddGroupButton {
      position: relative;
      height: 2.5rem;
      padding: 0;

      /* move the text to the right */
      .sapMBtnContentRight {
        padding-left: 2.875rem;
      }

      .sapMBtnPaddingRight {
        padding-right: 1.25rem;
      }
    }
  }
}

.sapUshellContainerIcon {
  font-size: 1rem;
}

.sapUshellTileContainerContent {
  .sapUshellNoFilteredItems {
    text-align: center;
  }

  .sapUshellInner:after {
    clear: both;
    content: "";
    display: block;
    height: 0;
    line-height: 0;
    visibility: hidden;
    padding: 0;
    margin: 0;
  }

  .sapUshellContainerTitleFlex {
    display: flex;
    height: 3.2rem;
    margin-right: 1rem;
    align-items: center;
    justify-content: space-between;
  }

  .sapUshellContainerTitle {
    cursor: default;
    display: inline;
    flex-shrink: 1;
    -webkit-user-select: none;
    -moz-user-select: none;
  }

  /* Tile Container Header - Input and buttons */

  .sapUshellTileContainerHeader {
    .sapUshellHeaderActionButton {
      position: static;
      padding: 0;
    }

    .sapUshellTileContainerHeader:focus {
      outline: 0.063rem dotted;
    }
  }

  .sapUshellCatalogTileContainerHeader {
    height: auto;
    margin-bottom: 1rem;
  }

  .sapUshellCatalogTileContainerHeaderInner {
    padding-top: 0.5rem;
  }

  .sapUshellTileContainerTitleInput {
    .sapMInputDivWrapper {
      height: 2.5rem;
    }

    input {
      border: none;
      padding: 0 0.5rem;
    }
  }

  .sapUshellContainerHeaderActions {
    margin-left: 1rem;
  }

  .sapUshellOverlayGroupActionPanel {
    display: flex;
    gap: 0.5rem;
  }

  &:focus {
    outline: none;
  }
}

.sapUiMedia-StdExt-Phone {
  .sapUshellTileContainerContent .sapUshellTileContainerTitleInput {
    width: 16rem !important;
  }
}

.sapUiMedia-Std-Tablet,
.sapUiMedia-Std-Desktop {
  .sapUshellTileContainerContent .sapUshellTileContainerTitleInput {
    width: 60% !important;
  }
}

.sapUshellDashboardGroupsContainerItem {
  margin-top: 1rem;
}

.sapUshellDashboardGroupsContainerItem,
.sapUshellDashboardGroupsContainerItem .sapUshellTileContainer,
.sapUshellDashboardGroupsContainerItem .sapUshellTileContainer .sapUshellTileContainerContent {
  &:focus {
    outline: none;
  }
}

.sapUshellDashboardGroupsContainerItem-clone .sapUshellLineModeContainer {
  display: none;
}

.sap-phone .sapUshellDashboardGroupsContainerItem-clone .sapUshellTileContainerContent .sapUshellContainerTitle {
  display: block;
}

.sapUiMedia-Std-Phone .sapUshellDashboardGroupsContainer .sapUshellTileContainerEditMode .sapUshellContainerTitle {
  height: auto;
  line-height: 2rem;
  padding: 0;
}

html[dir=rtl] .sapUshellDashboardGroupsContainer .sapUshellTileContainerContent {
  direction: rtl;
}

/* Only TileContainers located on the dashboard */
.sapUshellDashboardGroupsContainer .sapUshellContainerTitle {
  /* truncate group titles */
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* only TileContainers located on the dashboard */
.sap-phone .sapUshellDashboardGroupsContainer {
  .sapUshellTileContainerContent.sapUshellTileContainerEditMode {
    padding: 0;
  }

  .sapUshellContainerTitle {
    /* truncate group titles */
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }
}

.sapUshellTileContainerContent {
  margin-right: auto;
}

/* group focus is relevant for keyboard navigation, not relevant for mobile */
.sap-phone .sapUshellDashboardGroupsContainer .sapUshellTileContainerContent:focus,
.sap-tablet .sapUshellDashboardGroupsContainer .sapUshellTileContainerContent:focus {
  outline: 0;
}

#dashboardGroups .sapUshellDashboardGroupsContainerItem-placeholder {
  height: 2.75rem;
  margin-top: 0.5rem;
  margin-bottom: 1rem;
  border-radius: 0.25rem;
}

.sap-phone #dashboardGroups .sapUshellDashboardGroupsContainerItem-placeholder {
  margin-bottom: 4rem;
}

html[dir=rtl] .sapUshellDashboardGroupsContainerItem.sapUshellDashboardGroupsContainerItem-clone {
  direction: ltr;
}

.sapUshellDashboardGroupsContainerItem.sapUshellDashboardGroupsContainerItem-clone {
  align-items: center;
  padding-bottom: 6.25rem; /* for smooth scrolling */

  .sapUshellTileContainerEditMode {
    border: none;
    background-color: transparent;
  }

  .sapUshellCircleBase {
    visibility: hidden;
  }

  .sapUshellContainerTitle {
    padding: 0.5rem 1rem;
    border: 0.0625rem solid;
    border-radius: 0.375rem;
    text-overflow: ellipsis;
    overflow: hidden;
    display: inline-block;
    cursor: -webkit-grabbing;
  }

  .sapUshellTilesContainer-sortable,
  .sapUshellTileContainerBeforeContent,
  .sapUshellTileContainerAfterContent,
  .sapUshellContainerHeaderActions {
    display: none;
  }
}

#dashboardGroups .sapUshellDashboardGroupsContainerItem-placeholder .sapUshellTileContainer {
  display: none;
}

/* input field and Container title */
.sapUshellTileContainer&:not(.sapUshellEditing) .sapMInput,
.sapUshellTileContainer.sapUshellEditing .sapUshellContainerTitle {
  display: none;
  padding-right: 0.5rem;
  padding-left: 0.5rem;
  background-color: red;
}

.sapUshellTileContainerBeforeContent {
  margin-top: 1rem;
  margin-bottom: 1rem;
}

.sapUshellTileContainerAfterContent {
  button {
