/* START "WorkPageRow.less" */
[data-wp-breakpoint="st-sp-0"],
[data-wp-breakpoint="st-sp-1"],
[data-wp-breakpoint="st-lp-1"],
[data-wp-breakpoint="lt-sp-1"],
