      .workPageRowSectionTitle {
        padding-top: 0.5rem;
      }
    }
  }
}

.workPage.workPageHasTitle {
  .workPageRow.workPageRowFirst {
    padding-top: 0;
  }
}

.workPageRow {
  position: relative;
  margin-top: -2px;
  border: 2px solid transparent;
  border-radius: 0.75rem;
  box-sizing: content-box;
  background-clip: padding-box;
  padding-top: 1rem;
  margin-left: 1.75rem;
  margin-right: 1.75rem;

  .workPageRowMessageStrip {
    margin-top: 0.5rem;
    margin-bottom: 0.5rem;
    margin-left: 1.5rem;
    margin-right: 1.5rem;
  }

  .workPageRowToolbar {
    margin-left: 1rem;
    margin-right: 1rem;
    background-color: @sapUiShellBackgroundPatternColor;
    border-bottom: none;
  }

  .workPageRowSectionTitle {
    font-weight: bold;
    font-family: @sapUiFontFamily;
    font-size: @sapMFontHeader4Size;
    color: @sapUiShellGroupTitleTextColor;
    padding-left: 0.5rem;
    padding-right: 0.5rem;
    padding-top: 0.5rem;
  }

  & + .workPageRow {
    padding-top: 0;

    .workPageRowSectionTitle {
      padding-top: 2rem;
    }
  }

  .workPageRowLimiter {
    box-sizing: border-box;
    max-width: 100%;
    margin: 0 auto;
  }

  .workPageRowInner {
    position: static;
    display: grid;
  }

  .workPageRowButton {
    position: absolute;
    z-index: 1;
  }

  .workPageRowButtonBottom {
    bottom: 0;
    left: 0;
    right: 0;
    margin: auto;
    transform: translateY(~'calc(50% + 1px)');
  }

  .workPageRowButtonTop {
    top: 0;
    left: 0;
    right: 0;
    margin: auto;
    transform: translateY(~'calc(-50% - 1px)');
  }

  .workPageRowButtonLeft {
    top: 0;
    left: 0;
    bottom: 0;
    margin: auto;
    transform: translateX(-50%);
  }

  .workPageRowButtonRight {
    top: 0;
    right: 0;
    bottom: 0;
    margin: auto;
    transform: translateX(50%);
  }

  .workPageRowControlButtons {
    /* use opacity here to focus the button when the control is shown by tabbing into the WorkPageRow via keyboard navigation */
    opacity: 0;
    position: absolute;
    right: 0;
    transform: translateY(-2.5rem);
    padding-right: 2rem;
  }
}

.sapUiSizeCompact .workPageRow .workPageRowControlButtons {
  transform: translateY(-2rem);
}

.workPageRowToolbar > .sapMLabel .sapMLabelColonAndRequired::before {
  content: attr(data-colon);
  white-space: pre;
}


