/* EDIT MODE */
.workPageEditMode {
  &.workPage {
    .workPageRow,
    .workPageRow.workPageRowFirst {
      padding-top: 1rem;
    }
  }
  .workPageRow {

    &:hover,
    &.workPageRowFocused {
      border-color: @sapUiNeutralBorder;

      .workPageRowControlButtons {
        opacity: 1;
      }
    }

    .workPageRowInner {
      user-select: none;
    }

    &:hover + .workPageRow {
      border-top: none;
      margin-top: 0;
    }

    &:last-of-type:hover {
      border-bottom: 2px solid @sapUiNeutralBorder;
    }
  }
}

/* 1 Column */
[data-wp-cols="1"] {
  grid-template-columns: 1fr;
}


/* 2 Columns */
[data-wp-cols="2"] {
  grid-template-columns: 1fr 1fr;
}

[data-wp-breakpoint="st-sp-0"],
[data-wp-breakpoint="st-sp-1"],
[data-wp-breakpoint="st-lp-1"],
[data-wp-breakpoint="lt-sp-1"],
[data-wp-breakpoint="lt-lp-1"] {
