[data-wp-cols="2"] {
  &[data-wp-col-min-flex="1"],
  &[data-wp-col-min-flex="2"] {
    &[data-wp-grid-layout="2-22"] { grid-template-columns: 2fr 22fr; }
    &[data-wp-grid-layout="4-20"] { grid-template-columns: 4fr 20fr; }
    &[data-wp-grid-layout="6-18"] { grid-template-columns: 6fr 18fr; }
    &[data-wp-grid-layout="8-16"] { grid-template-columns: 8fr 16fr; }
    &[data-wp-grid-layout="10-14"] { grid-template-columns: 10fr 14fr; }
    &[data-wp-grid-layout="12-12"] { grid-template-columns: 12fr 12fr; }
    &[data-wp-grid-layout="14-10"] { grid-template-columns: 14fr 10fr; }
    &[data-wp-grid-layout="16-8"] { grid-template-columns: 16fr 8fr; }
