/* START "WorkPageColumn.less" */
.workPageColumn {
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: stretch;
  justify-content: stretch;
  max-width: 100%;
  box-sizing: border-box;
  padding-top: 1rem;
  padding-bottom: 1rem;
  min-width: 0;

  [data-wp-breakpoint="st-sp-1"] &,
  [data-wp-breakpoint="st-sp-0"] & {
    min-width: 100%;
  }

  .workPageColumnDeleteButton {
    position: absolute;
    right: 1.5rem;
    top: 1.25rem;
  }

  .workPageColumnToolbar {
    margin-top: 0.5rem;
    display: flex;
    gap: 0 0.5rem;
    flex-direction: row;
    justify-content: center;
    flex-wrap: wrap;
  }

  &.workPageColumnEmpty .workPageAddWidgetButton {
    position: absolute;
    top: 0;
    bottom: 0;
    margin: auto;
  }
}

/* END "WorkPageColumn.less" */

/* START "WorkPageCell.less" */
.workPageCell {
  flex: 0;
  display: flex;
  flex-direction: column;
  border-radius: @sapUiElementBorderCornerRadius;
  margin-left: 0.5rem;
  margin-right: 0.5rem;

  [data-wp-breakpoint="st-sp-0"] &,
  [data-wp-breakpoint="st-sp-1"] &,
  [data-wp-breakpoint="st-lp-1"] &,
  [data-wp-breakpoint="lt-sp-1"] & {
    margin-left: 0;
    margin-right: 0;
  }

  &.workPageCellEmpty {
    background-color: transparent;
    flex: 1;
  }

  &+.workPageCell {
    margin-top: 1rem;
  }

  .workPageEditMode .workpageCellWidget {
    pointer-events: none;
  }

  .workpageCellWidget {
    flex: 1 1 auto;
  }

  .workpageCellWidgetToolbar {
    position: relative;
    justify-content: flex-end;
    gap: 0.25rem;
    border-radius: 1rem;
    z-index: 1;
  }

  .workPageGridContainer {
    z-index: 0;
  }

  .sapUshellVizInstance {
    position: relative;

    .sapMGT {
      max-width: 100%;
    }
  }

  .workPageCellControlButtons {
    opacity: 0;
  }

  .workPageCellOverflowToolbar {
    border-radius: 1rem 1rem 0 0;
    padding-right: 1rem;
  }

  &.workPageCellCardMode {
    .workPageCellOverflowToolbar {
      background: transparent;
      border-bottom: none;
    }
  }
}

.workPageCell.workPageCellTileMode {
  box-shadow: @sapUiContentShadow0;

  .workPageGridContainer {
    padding-top: 1rem;
    padding-bottom: 1rem;
  }
}

.workPageCell.workPageCellTileMode {
  .workPageGridContainer {
    padding-left: 0.25rem;
    padding-right: 0.25rem;
  }
}

