/* EDIT MODE */
.workPageEditMode {

  .workPageCell {
    &.workPageCellEmpty {
      background-color: @sapUiShellContainerBackground;
      min-height: 14rem;
    }

    &.workPageCellTileMode {

      &:hover,
      &.workPageCellFocused {
        box-shadow: @sapUiContentShadow2;
      }
    }

    &:hover,
    &.workPageCellFocused {
      .workPageCellControlButtons {
        opacity: 1;
      }
    }
  }
}

/* END "WorkPageCell.less" */

/* START "WorkPageColumnResizer.less" */
.workPageDivider {
  position: absolute;
  transform: translateX(-50%);
  top: 0;
  bottom: 0;
  width: 2px;
  left: 0;
  padding: 4px;

  &:hover {
    cursor: col-resize;
  }

  .workPageDividerInner {
    position: absolute;
    top: 0;
    bottom: 0;
    width: 2px;
    border-right: 2px dashed @sapUiNeutralBorder;
    transform: translateX(-2px);
  }
}

html[dir=rtl] .workPageDivider {
  transform: translateX(50%);

  .workPageDividerInner {
    transform: translateX(2px);
  }
}

.workPageDividerButton {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  margin-top: auto;
  margin-bottom: auto;
  transform: translateX(-50%);
  z-index: 1;

  &.workPageColumnButtonRight {
    left: auto;
    right: 0;
    transform: translateX(calc(~"0.5rem + 6.5px"));
  }

  &.workPageColumnButtonLeft {
    transform: translateX(calc(~"-0.5rem - 6.5px"));
  }
}


html[dir=rtl] .workPageDividerButton {
  &.workPageColumnButtonRight {
    left: auto;
    right: 0;
    transform: translateX(calc(~"-0.5rem - 6.5px"));
  }

  &.workPageColumnButtonLeft {
    transform: translateX(calc(~"0.5rem + 6.5px"));
  }
}

