/* ============================= */
/* CSS for control sap.m/Slider  */
/* Base theme                    */
/* ============================= */

/* ------------------------------ */
/* Slider commons styles          */
/* ------------------------------ */

.sapVizSlider {
  display: inline-block;
  vertical-align: top;
  box-sizing: border-box;
  position: relative;
  height: 3.3125rem;
  padding: 1.406rem 1.0625rem;


  /* IE Windows 8 */
  /* This elements receives events when not panning in vertial direction */
  touch-action: pan-y;
  -ms-touch-action: pan-y;
}

.sapVizSliderInner {
  width: 100%;
  z-index: 1;
  position: relative;
  background-repeat: no-repeat;
}

.sapVizSliderInner,
.sapVizSliderProgress {
  height: 0.188rem;
}

.sapVizSliderProgress,
.sapVizSliderHandle {
  position: absolute;
}

.sapVizSliderHandle {
  display: block;
  outline: none;
}

.sapVizSliderInput {
  display: none;
}

.sapVizSliderTickmarks {
  margin: 0;
  padding: 0;
  list-style: none;
  position: absolute;
  z-index: 0;
  top: 0.625rem;
  left: 1.0625rem;
  right: 1.0625rem;;
  display: block;
  height: 1.75rem;
}

.sapVizSliderTick {
  display: inline-block;
  height: 1rem;
  border-left: 0.0625rem solid @sapUiFieldBorderColor;
  margin: 0.4375rem 0 0 -0.0625rem;
}


.sapVizSliderTickLabel {
  display: inline-block;
  position: absolute;
  z-index: 1;
  width: 0.0625rem;
  background-color: @sapUiFieldBorderColor;
  margin: 0 0 0 -0.0625rem;

  &:last-child {
    right: 0;
  }
}

.sapVizSliderLabel {
  font-size: 0.75rem;
  display: block;
  width: 5rem;
  text-align: center;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  position: absolute;
  top: 1.75rem;
  left: -2.5rem;
  direction: ltr;
}

/* slider progress bar */
.sapVizSliderProgress {
  background: @sapUiFieldActiveBorderColor;

  &:before{
    content: '';
    position: absolute;
    width: 100%;
    height: 500%;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
  }
}

/* slider handle */
.sapVizSliderHandle {
  margin-left: -1rem;
  top: -0.91rem;
  height: 1.75rem;
  width: 1.75rem;
  direction: ltr;

  /* some browsers like Samsung Galaxy S4 with Android 4.2 does not support the "border-radius" shorthand property
  but does support the long-hand properties for each corner like "border-top-left-radius" */
  border-top-left-radius: 1rem;
  border-top-right-radius: 1rem;
  border-bottom-right-radius: 1rem;
  border-bottom-left-radius: 1rem;

  &:before{
    content: '';
    position: absolute;
    width: 100%;
    left: 50%;
    top: 40%;
    transform: translate(-50%, -50%);
    cursor: pointer;
  }
}

.sapVizSliderHandle:hover ~ .sapVizSliderTooltipContainer{
  visibility: visible;
}

.sapVizSliderHandleTooltip:hover{
  visibility: visible;
}

.sapMInputBaseWidthPadding:hover{
  visibility: visible;
}

.sapVizSliderProgress:hover ~ .sapVizSliderTooltipContainer {
  visibility: visible;
}

/* ============ */
/* compact size */
/* ============ */

/* slider */
.sapUiSizeCompact {
  .sapVizSlider {
    height: 2rem;
    padding-top: 1rem;
    padding-bottom: 1rem;
  }

  /* slider handle */
  .sapVizSliderHandle {
    height: 1.375rem;
    width: 1.375rem;
    margin-left: -0.8125rem;
    top: -0.75rem;
  }

  .sapVizSliderTickmarks {
    height: 1rem;
  }

  .sapVizSliderLabel {
    top: 1rem;
  }

  .sapVizSliderTick {
    margin-top: 0.125rem;
    height: 0.75rem;
  }

  .sapVizSliderTickLabel {
    height: 1rem;
  }
}
/* ================================== */
/* CSS for control sap.m/RangeSlider  */
/* Base theme                         */
/* ================================== */

.sapVizSliderProgress {
  width: auto;
}

.sapVizSliderProgress:focus {
  outline: none;
}

html.sap-desktop .sapVizSliderProgress:focus:before {
  border: 1px dotted @sapUiContentFocusColor;
  content: "";
  display: block;
  position: absolute;
  bottom: -2px;
  right: -2px;
  pointer-events: none;
}

.sapVizSliderHandleTooltip {
  display: inline-block;
  position: absolute;
}

//----- RangeSlider Labels------------
.sapVizSliderLabels {
  margin: 0.875rem -0.875rem 0 -0.875rem;
  height: 1rem;
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  justify-content: space-between;
  margin-left: -1.0625rem;
  margin-right: -1.406rem;
}

.sapVizSliderRangeLabel {
  display: block;
  width: 1.75rem;
  font-size: 0.75rem;
  align-self: flex-end;
  text-align: center;
  direction: ltr;
}

.sapVizSliderTooltipContainer {
  display: flex;
  position: absolute;
  top: -3rem;
  height: 2rem;
  width: auto;
  justify-content: space-between;
  transition-property: left, right;
  visibility: hidden;
}

//----- RangeSlider Input Labels------------
.sapVizSliderTooltipContainer .sapMInputBaseWidthPadding {
  padding: 0;
}

.sapVizSliderTooltipContainer {
  .sapMInputBase {
    height: inherit;
  }
  .sapMInputBaseInner {
    height: inherit;
    font-size: 0.75rem;
    position: relative;
    padding-left: 0.5rem;
    padding-right: 0.5rem;
  }
  .sapMInputBaseState .sapMInputBaseStateInner {
    height: inherit;
    font-size: 0.75rem;
    position: relative;
    padding-left: 0.5rem;
    padding-right: 0.5rem;
  }
  .sapMInputBaseState .sapMInputBaseErrorInner {
    visibility: visible;
  }
}

.sapUiSizeCozy .sapVizSliderTooltipContainer {
  .sapMInputBase {
    height: 3rem;
    top: -1rem;
    padding: 0.25rem 0;
  }
  .sapMInputBaseInner {
    height: 2.5rem;
  }
}

/* pressed */

.sapVizSliderHandleTooltip {
  padding-left: 0.5rem;
  padding-right: 0.5rem;
  height: 1.5rem;
  line-height: 1.5rem;
  font-size: 0.75rem;
  text-align: center;
  visibility: hidden;
  box-shadow: @sapUiShadowLevel1;
  position: relative;
  flex-shrink: 0;
  background-color: @sapUiGroupContentBackground;
  direction: ltr;
}

.sapVizSliderHandleTooltipShow {
  visibility: visible;
}

.sapVizSliderHandle:hover ~ .sapVizSliderTooltipContainer .sapVizSliderHandleTooltip{
  visibility: visible;
}

.sapVizSliderProgress:hover ~ .sapVizSliderTooltipContainer .sapVizSliderHandleTooltip {
        visibility: visible;
      }

.sapVizSliderHandleTooltipsShow,
.sapVizSliderHandleTooltipsShow .sapVizSliderHandleTooltip {
  visibility: visible;
}

/* overlap */
.sapVizSliderHandle.sapVizSliderHandleOverlap {
  background-color: @sapUiFieldActiveBorderColor;
  border: @sapUiFieldActiveBorderColor;
}

/* pressed */
.sapVizSliderInner > .sapVizSliderHandle.sapVizSliderHandlePressed {
  background: @sapUiHighlight;
  border: 0.125rem solid @sapUiFieldActiveBorderColor;
}


/* ============ */
/* compact size */
/* ============ */
.sapUiSizeCompact {
  .sapVizSliderLabels {
    margin: 0.5rem -0.6875rem 0 -0.6875rem;
  }

  .sapVizSliderRangeLabel {
    width: 1.375rem;
  }
}







