	border-bottom: 1px solid #cdcdcd;
	border-right: 1px solid #cdcdcd;
	overflow: hidden !important;
}

div.zenborder{
	overflow: visible !important;
}

.sapzenfilterbar .sapzenfilterpanel-HasLabelOnTop{
	margin-top:20px;
}

button.zenSapMBtnFixHeight{
	padding-top:0px;
}

button.zenSapMBtnFixHeight .sapMBtnContent{
    position: absolute;
    top: 50%;
    left: 0.75rem;
    transform: translateX(0%) translateY(-50%);
    -ms-transform: translateX(0%) translateY(-50%); /* IE 9 */
    -webkit-transform: translateX(0%) translateY(-50%); /* Chrome, Safari, Opera */
    right: 0.75rem;
}

button.zenSapMBtnFixHeight .sapMBtnInner{
	height:100%;
	width:100%;
}

.sapUiSizeCompact button.zenSapMBtnFixHeight{
	padding-top:0px;
}


.zenMessageWithOpacity {
	background: @sapUiFieldReadOnlyBackground;
}

.hideLoadingStateClass{
	visibility: hidden;
}
.hideLoadingStateClassIcon{
	visibility: hidden;
}
.componentLoadingState{
	background-color: @sapUiMediumBG;
	width:100%;
	height:100%;
	position:absolute;
	top:0px;
}

.zenHideFilterPanel {
	display: none;
}

.zenLoadingStateOpacity{
		opacity:0.5;
}
.zenLoadingStateFilter{
		filter: alpha(opacity=50);
}

.zenLoadingStateOpacity75{
		opacity:0.75;
}
.zenLoadingStateFilter75{
		filter: alpha(opacity=75);
}

.componentLoadingStateMessage{

	font: 10.5pt, arial;

	position:absolute;	

	text-align: center;
	text-vertical-align: middle;

	-moz-box-sizing: border-box; -webkit-box-sizing: border-box; box-sizing: border-box;

	padding-left: 13px;
	padding-right: 13px;
	padding-top: 43px;

	margin-top: -45px;
	margin-left: -100px;

	left: 50%;
	top: 50%;

	width: 200px;
	height: 90px;		

	background-color: transparent !important;

	white-space: nowrap;
	overflow: hidden  !important;
	text-overflow: ellipsis ;

	color: @sapUiBase;
}

.componentLoadingStateMessageWithIcon{

	font: 10.5pt, arial;

	position:absolute;	

	text-align: center;
	text-vertical-align: middle;

	-moz-box-sizing: border-box; -webkit-box-sizing: border-box; box-sizing: border-box;

	padding-left: 13px;
	padding-right: 13px;
	padding-top: 50px;

	margin-top: -45px;
	margin-left: -100px;

	left: 50%;
	top: 50%;

	width: 200px;
	height: 90px;		

	background-color: transparent !important;

	white-space: nowrap;
	overflow: hidden  !important;
	text-overflow: ellipsis ;

}


.componentLoadingStateBox {

	font-size: 10.5pt;

	position:absolute;	

	text-align: center;
	text-vertical-align: middle;

	-moz-box-sizing: border-box; -webkit-box-sizing: border-box; box-sizing: border-box;

	padding-top: 43px;
	padding-left: 13px;
	padding-right: 13px;

	margin-top: -45px;
	margin-left: -100px;

	left: 50%;
	top: 50%;

	width: 200px;
	height: 90px;

	background-color: @sapUiWhite !important;

}
.componentLoadingStateBox.componentLoadingStateBoxNoText.componentLoadingStateBoxNoIcon {
	display:none;
	}


.componentLoadingStateMessageDT{	
background-color:white;
}

.componentLoadingStateIcon{
	background-image: url("../base/img/loading_comp.gif");
	background-repeat:no-repeat;
	position: absolute;
	left: 50%;
	top: 50%;
	width: 65px;
	height: 19px;
	margin-top: -20px;
	margin-left: -35px;
	opacity:1 !important;
}

.componentLoadingStateBoxNoText + .componentLoadingStateIcon{
		background-image: url("../base/img/loading_comp.gif");
	background-repeat:no-repeat;
	position: absolute;
	left: 50%;
	top: 50%;
	width: 65px;
	height: 19px;
	margin-top: -5px;
	margin-left: -35px;
	opacity:1 !important;
}


.hideLoadingStateClass + .componentLoadingStateIcon{
	background-image: url("../base/img/loading_comp.gif");
	background-repeat:no-repeat;
	position: absolute;
	left: 50%;
	top: 50%;
	width: 65px;
	height: 19px;
	margin-top: -7px;
	margin-left: -34px;
	opacity:1 !important;
}

.sapDataboundComponentLoadingIndicator{
	margin: -20px 0px 0px -100px;
}

.sapFixIEBugThatMakesImageControlTwoPixelsTooHigh {
	margin-bottom: -2px;
}

.zenClickable {
cursor: pointer;
}

.customGlasspaneZenClass {
	position: fixed;
	top: 0px;
  left: 0px;
	width: 100%;
	height: 99%;
	z-index: 9999;
	vertical-align: middle;
	text-align: center;
	cursor: wait;
	background-color: @sapUiBase;
	display: block;
}

.zenGlasspaneOpacity{
		opacity:0.3;
}

.zenGlasspaneFilter{
	filter: alpha(opacity=50);
}

.zenPixelClass{
	width:100%; height:100%;
}

/* Begin of Dialog styles */

.zenDialogMatrixLayout {
    overflow: hidden;
    padding: 0px;   
}

/* BUTTONS *********************************/
.zenDialogButtonArea {
    padding: 0 5px 0px 5px !important;
    height: 21px;
}

.zenDialogButtonRow {
	height: 21px;
}

/* CONTENT *********************************/
.zenDialogContentArea {
    padding: 5px !important;
    height: auto;
}

.zenDialogContentRow {
	height: auto;
}


.zenChartImage {
	position: absolute;
	opacity:0.50;
}


.bookmarkOpenButton{
	padding-left:10px;
}

.shareBookmarkDialogContainer .sapUiDlgCont{
	padding:14px;
}

.shareBookmarkDialogContainer .sapUiTf{
	margin-right: 4px;
	margin-bottom: 5px;
}
/* End of Dialog styles */

/***Dialog**********************************/
.uidialogzentitle{
	position: absolute;
	height: 2.5rem !important;
	top: 0;
  background-color: #f2f2f2;
  filter: none;
  background-image: none;
  font-size: 1rem;
  color: #666;
  text-shadow: 0 0.0625rem 0 rgba(255,255,255,0.5);
  -webkit-box-shadow: inset 0 -0.0625rem #dddddd;
  box-shadow: inset 0 -0.0625rem #dddddd;
}

.uidialogzenfooter{
	position: absolute;
	height: 2.5rem !important;
	bottom: 0;
	background-color: rgba(54,62,67,0.96);
	color: #ffffff;
  text-shadow: 0 0.0625em 0 rgba(0,0,0,0.5);
  background-color: rgba(54,62,67,0.96);
  background-image: none;
  border-top: 0.0625rem solid rgba(229,229,229,0.2);
  -webkit-box-shadow: none;
  box-shadow: none;
}

.uidialogzencontent{
	position: absolute;
	padding-top: 2.5rem;
	padding-bottom:2.5rem;
	background: #fafafa;
}

.uidialogzenroot{
		border: 1px solid #CCC;
}

.uidialogzenroot  > .sapUiLayoutAbsPos:not(:nth-child(2)){
	height: auto !important;
}
.uidialogzenroot  > .sapUiLayoutAbsPos:nth-child(2){
	height: calc(100% - 5rem) !important;
}

.uidialogzentitle > .sapUiLayoutAbsPos{
	width: 100% !important;
	height: 100% !important;
}

.uidialogzentitle > .sapUiLayoutAbsPos > span{
  text-align: center !important;
  position: absolute;
  margin: auto;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
  width: 50%;
  height: 50%;
}
/***done Dialog**********************************/


