/* =============================== */
/* CSS for control sap.f/GridList  */
/* Evening Horizon theme           */
/* =============================== */

@_sap_f_GridList_ItemFocusBorderRadius: 1rem;
@_sap_f_GridList_ItemFocusedHighlightOffset: 0.5rem;
@_sap_f_GridList_ItemHighlightBorderRadius: 1rem 2rem;
@_sap_f_GridList_ItemOverflow: hidden;

.sapFGridList > .sapMGrowingList {
	background: none;
}
/* END "GridList.less" */

/* START "GridListItem.less" */
/* =================================== */
/* CSS for control sap.f/GridListItem  */
/* Evening Horizon theme               */
/* =================================== */

@_sap_f_GridListItem_HeaderBoxShadow: none;
@_sap_f_GridListItem_Overflow: hidden;
@_sap_f_GridListItem_HeaderHeight: 2.75rem;
@_sap_f_GridListItem_FocusedHighlightOffset: 0.5rem;
@_sap_f_GridListItem_HighlightBorderRadius: 1rem;
@_sap_f_GridListItem_FocusedHighlightBorderRadius: 1rem 2rem;
/* END "GridListItem.less" */

/* START "ShellBar.less" */
/* =============================== */
/* CSS for control sap.f/ShellBar  */
/* Evening Horizon theme           */
/* =============================== */

.alwaysCozyOverrideByParent(~"sapFShellBar");

.alwaysDarkModeOverrideByParent(~"sapFShellBar");

.sapFShellBarSizeLargeDesktop,
.sapFShellBarSizeExtraLargeDesktop {
	padding: 0 3rem;
}

.sapFShellBar .sapFShellBarSearchIsOpen .sapFShellBarSearchWrap .sapMBtn:not(.sapFShellBarSearchCancelButton) {
	right: 2.75rem;
}

.sapFShellBarSizeDesktop {
	padding: 0 2rem;
}

.sapFShellBar.sapFShellBarFCLFPHeader,
.sapMPageHeader .sapFShellBar {
	box-shadow: var(--sapShell_Shadow);
}

.sapFShellBar {
	background: var(--sapShellColor);
	height: 3.25rem;
	box-shadow: inset 0 -0.0625rem 0 0 var(--sapShell_BorderColor);

	.sapFShellBarOTB.sapMIBar.sapMTB {
		height: 3.25rem;
	}

	/* Copilot */
	.CPImage,
	& .sapMIBar.sapMTB .CPImage {

		&::after {
			position: absolute;
			content: '';
			left: 3px;
			right: 3px;
			top: 3px;
			bottom: 3px;
			outline: none;
			pointer-events: none;
			border-radius: 0.5rem;
		}

		.color1 {
			stop-color: #0070F2;
		}

		.color2 {
			stop-color: #EBF8FF;
		}

		.opacity7 {
			stop-opacity:0.7;
		}

		.opacity36 {
			stop-opacity: 0.36;
		}

		.opacity2 {
			stop-opacity: 0.2;
		}

		&:focus {
			outline: var(--sapContent_FocusWidth) solid var(--sapContent_FocusColor);
			border-radius: 0.375rem;
			background-color: var(--sapShell_Active_Background);
			color: var(--sapShell_Active_TextColor);

			&::after {
				box-shadow: var(--sapContent_Selected_Shadow);
			}
		}

		&:hover:not(:focus) {
			background: var(--sapShell_Hover_Background);

			&::after {
				box-shadow: var(--sapContent_Interaction_Shadow);
			}
		}
	}

	&.sapMPageHeader .sapMIBar,
	.sapMIBar {
		height: 3.25rem;
	}

	.sapMBtn:hover > .sapMBtnHoverable.sapMToggleBtnPressed:not(.sapMBtnActive) {
		border-color: var(--sapShell_Hover_Background);
	}

	.sapMToggleBtnPressed:not(.sapMBtnDisabled),
	.sapMTB-Transparent-CTX .sapMBtnInner.sapMToggleBtnPressed:not(.sapMBtnDisabled) {
		background-color: var(--sapShell_Active_Background);
		border-color: var(--sapShell_Active_Background);
	}

	.sapFShellBarProfile:not(:focus):hover {
		box-shadow: 0 0 0.125rem 0 fade(@sapContent_ShadowColor, 72);
	}

	/* Mega Menu */
	.sapFSHMegaMenu {
		color: var(--sapShell_TextColor);
		overflow: visible;
		font-size: var(--sapFontHeader5Size);
		font-weight: bold;

		.sapMBtn:not(:hover) .sapMBtnInner.sapMBtnTransparent::after,
		.sapMBtnTransparent {
			color: var(--sapShell_TextColor);
			background: var(--sapButton_Lite_Background);
		}

		.sapMBtn:hover > .sapMBtnTransparent.sapMBtnHoverable:not(.sapMBtnActive):not(.sapMToggleBtnPressed) bdi,
		&.sapMMenuBtn.sapMMenuBtnRegular .sapMBtnInner.sapMBtnTransparent:not(.sapMBtnActive)::after {
			color:  var(--sapShell_TextColor);
		}

		.sapMBtn:hover > .sapMBtnHoverable:not(.sapMBtnActive) {
			background: var(--sapShell_Hover_Background);
			border: 1px solid var(--sapButton_Lite_Hover_BorderColor);
			color: var(--sapShell_TextColor);
		}

		.sapMBtn:hover > .sapMBtnHoverable.sapMBtnActive {
			background: var(--sapShell_Active_Background);
			border: 1px solid var(--sapButton_Lite_Active_BorderColor);
			color: var(--sapShell_Active_TextColor);
		}

		.sapMBtnInner,
		.sapMBtnInner::after {
			font-weight: normal;
			font-size: 1rem;
		}

		.sapMBtnInner::after {
			content: '\e1ef';
			font-family: var(--sapContent_IconFontFamily);
		}
	}

	/* Home Icon */
	.sapFShellBarHomeIcon {
		max-height: 2rem;
		padding: 0.25rem;
		overflow-clip-margin: border-box;

		&:hover {
			background: var(--sapShell_Hover_Background);
			box-shadow: var(--sapContent_Interaction_Shadow);
			border-radius: 0.5rem;
		}

		&:focus {
			outline-offset: -0.125rem;
		}
	}

	/* Primary Title */
	.sapFShellBarPrimaryTitle {
		color: var(--sapShell_SubBrand_TextColor);
	}

	/* Second Title */
	.sapFShellBarSecondTitle {
		font-size: var(--sapFontSmallSize);
		color: var(--sapShell_TextColor);
		text-overflow: ellipsis;
	}

	/* Search */
	.sapFShellBarSearch {
		max-width: 35rem; /* max width of searchField + button width */

		.sapMSF {
			max-width: 35rem;
		}

		/* Reflection: styles overwritten */
		.sapMBtn:not(.sapFShellBarSearchCancelButton) {
			width: 1.75rem;
			height: 1.75rem;
			line-height: 1.75rem;
			padding: 0;
			margin-top: auto;
			margin-bottom: auto;
			margin-left: 0.25rem;
			position: absolute;
			right: 0;
			top: 0;
			bottom: 0;

			.sapMBtnInner {
				min-width: 1.75rem;
				border-radius: 1.125rem;
				color: var(--sapButton_Emphasized_TextColor);

				.sapMBtnIcon {
					width: 1.75rem;
					height: 1.75rem;
					line-height: 1.75rem;
				}
			}
		}

		.sapMSFVal + .sapMBtn .sapMBtnInner {
			background-color: var(--sapButton_Emphasized_Background);

			.sapMBtnIcon {
				color: var(--sapButton_Emphasized_TextColor);
			}
		}
	}

	.sapFShellBarSearch.sapFShellBarSearchFullWidth .sapMBtn:not(.sapFShellBarSearchCancelButton) {
		right: 6rem;
	}

	.sapFShellBarSearch.sapFShellBarSearchFullWidth .sapFShellBarSearchWrap .sapMSFF .sapMSFB {
		margin-right: 1.75rem;
	}

	.sapFShellBarSearch.sapFShellBarSearchFullWidth .sapFShellBarSearchWrap {
		max-width: 35rem;
	}

	.sapFShellBarSearch.sapFShellBarSearchFullWidth .sapFShellBarSearchWrap .sapMSF.sapMSFVal form.sapMSFNS:not(.sapMSFReload) {
		padding-right: 0.5rem;
	}

	.sapFShellBarSearchWrap {
		position: relative;

		.sapMSFF {
			background: var(--sapField_BackgroundStyle);
			background-color: var(--sapShell_InteractiveBackground);
			box-shadow: var(--sapField_Shadow);
			border: none;
			color: var(--sapShell_InteractiveTextColor);
			border-radius: 1.125rem;
			height: 2.25rem;

			.sapMSFB {
				width: 1.75rem;
				height: 1.75rem;
				line-height: 1.75rem;
				font-size: 1rem;
				text-align: center;
				margin-top: 0.25rem;
				margin-right: 2.25rem;
				padding: 0;
				right: 0;
				top: 0;
			}

			.sapMSFB:hover {
				background: var(--sapShell_Hover_Background);
				box-shadow: var(--sapContent_Interaction_Shadow);
				border-radius: 1.125rem;
				height: 1.75rem;
				color: var(--sapShell_InteractiveTextColor);
			}

			.sapMSFB:active,
			.sapMSFB.sapMSFBA {
				background: var(--sapShell_Active_Background);
				box-shadow: var(--sapContent_Selected_Shadow);
				border-radius: 1.125rem;
				color: var(--sapShell_Active_TextColor);
			}
		}

		.sapMSF {
			max-width: 35rem;
			margin-right: 0;
		}

		.sapMSF:not(.sapMSFVal) form.sapMSFNS:not(.sapMSFReload) {
			padding-right: 0.5rem;
		}

		.sapMSF:not(.sapMSFDisabled):hover .sapMSFF {
			background-color: var(--sapShell_Hover_Background);
			box-shadow: var(--sapField_Hover_Shadow);
		}

		.sapMSFI {
			color: var(--sapField_PlaceholderTextColor);
			font-weight: italic;
		}

		/* sap.m.Button
		Reflection: styles overwritten */
		.sapMBtn:not(.sapFShellBarSearchCancelButton) {
			height: 2.25rem;
			line-height: 2.25rem;
			width: 1.75rem;
			padding: 0.25rem 0;
			margin-right: 0.25rem;

			.sapMBtnInner {
				height: 1.75rem;
				min-width: 1.75rem;
				border-radius: 1.125rem;
			}

			.sapMBtnInner.sapMBtnText {
				padding-left: 0.625rem;
				padding-right: 0.625rem;
			}

			.sapMBtnIcon {
				width: 1.75rem;
				font-size: 1rem;
				line-height: 1.75rem;
				max-height: 1.75rem;
				margin: 0;
				padding: 0;
			}
		}

		.sapMSF {
			margin-right: 0;
		}
	}
}

/* Overriding styles comming from other controls */
html.sap-desktop .sapFShellBarSearchWrap .sapMBtn:not(.sapFShellBarSearchCancelButton):focus > .sapMFocusable:not(.sapMBadgeIndicator)::before {
	border-radius: 1.125rem;
}

html.sap-desktop .sapMOverflowToolbarMenu-CTX .sapFShellBarSearchWrap .sapMBtn:not(.sapFShellBarSearchCancelButton):focus > .sapMFocusable:not(.sapMBadgeIndicator)::before {
	border-radius: 0.375rem;
}

