			}

			/* Reflection: styles overwritten
			sap.m.SearchField */
			.sapFShellBarSearch {
				max-width: 35rem;

				.sapMSF {
					max-width: 35rem;
				}

				.sapMBtn:not(.sapFShellBarSearchCancelButton) {
					width: 1.75rem;
					height: 1.75rem;
					line-height: 1.75rem;
					padding: 0;
					margin-top: auto;
					margin-bottom: auto;
