			}

			.sapMSF {
				min-width: 7rem;
				height: 2.75rem;
				padding: 0.25rem 0;

				.sapMSFI {
					height: 2.125rem
				}

				.sapMSFF {
					height: 2.25rem;
					line-height: 2.125rem;
					padding-left: 0.625rem;
				}

				.sapMSFR:not(.sapMSFNS) {
