}

.sapMBar .sapMBarRight.sapMBarContainer > *:first-child:last-child,
.sapMBar .sapMBarLeft.sapMBarContainer > *:first-child:last-child,
.sapMBar .sapMBarMiddle .sapMBarContainer > *:first-child:last-child {
	margin: 0;
}

.sapMBar {
	height: 2.75rem;
	line-height: 2.75rem;
}

.sapMBarLeft,
.sapMBarRight,
.sapMBarPH {
	height: 2.75rem;
