}

.sapMDialogTitleGroup {
	z-index: 3; /* to hide the shadow from the header when there is sub-header */
}

/* buttons on phone must be to the right side (in LTR mode) */
html.sap-phone .sapMDialog {

	& > footer .sapMFooter-CTX {
		justify-content: flex-end;
	}
}

/* ========================================================================== */
/* header, sub-header, footer */
/* ========================================================================== */

.sapMDialog {

	header .sapMBarLeft,
	header .sapMBarPH {
		padding-left: 1rem;
	}

	header .sapMBarRight,
	header .sapMBarPH {
		padding-right: 1rem;
	}

	header .sapMTB,
	footer .sapMTB {
		padding: 0 1rem;
	}
}

/* Responsive paddings */
.sapMDialogTitleGroup,
.sapMDialogSubHeader {

	.sapUi-Std-PaddingS,
	.sapUi-Std-PaddingM,
	.sapUi-Std-PaddingL,
	.sapUi-Std-PaddingXL {

		&.sapMIBar {

			.sapMBarMiddle .sapMBarPH {
				padding-left: 0;
				padding-right: 0;
			}

			.sapMBarLeft {
				padding-left: inherit;

				.sapMBarChild {
					padding-right: 0.5rem;
				}
			}

			.sapMBarRight {
				padding-right: inherit;

				.sapMBarChild {
					padding-left: 0.5rem;
				}
			}
		}
	}
}

.sapMDialog:not(.sapUiResponsiveContentPadding):not(.sapUiContentPadding) {

	&.sapMDialog-NoHeader .sapMDialogScroll {
		padding-top: 0.25rem;

		html.sap-phone .sapMDialogStretched& {
			padding-top: 0;
		}
	}

	&.sapMDialog-NoFooter .sapMDialogScroll {
		padding-bottom: 0.25rem;

		html.sap-phone .sapMDialogStretched& {
			padding-bottom: 0;
		}
	}
}
