/* Head Container */
.sapMITH {

	z-index: 1; /* shows the bottom shadow of the ITHeader over the content of the ITB */

	.sapMITBHead {
		padding-top: 0.875rem;
	}

	&.sapMITBNoText {

		.sapMITBHead {
			padding-top: 1rem;
		}
	}

	&.sapMITBTextOnly {

		.sapMITBHead {
			padding-top: 0.875rem;
		}
	}

	&.sapMITBInLine {

		.sapMITBHead {
			padding-top: 0;
		}
	}

	.sapMITBDisabled {

		.sapMITHTextContent {
			color: var(--sapTextColor);
			font-family: var(--sapFontBoldFamily);
			font-size: @sapMFontMediumSize;
		}
	}

	.sapMITBSelected {

		.sapMITBContentArrow::after {
			background: var(--sapTab_ForegroundColor);
		}
	}

	.sapMITBFilterExpandBtnSeparator {
		border-right: 0.0625rem solid;
		margin-left: 0.625rem;
		border-color: @_sap_m_IconTabBar_TextColor;
		height: 0.625rem;
		margin-top: 1.0625rem;
	}

	/* More Button */
	.sapMITHStartOverflow,
	.sapMITHEndOverflow {

		.sapMITBItem {
			color: var(--sapButton_TextColor);
			background: var(--sapButton_Background);
			border: 1px solid var(--sapButton_BorderColor);

			.sapMITHShowSubItemsIcon {
				color: var(--sapTextColor);
			}
		}
	}

	.sapMITHEndOverflow:hover,
	.sapMITHStartOverflow:hover {

		.sapMITBItem {
			background: var(--sapButton_Hover_Background);
			border: 1px solid @_sap_m_IconTabBar_Overflow_BorderColorHover;
		}
	}

	.sapMITHEndOverflow,
	.sapMITHStartOverflow {

		.sapMITHShowSubItemsIcon {
			padding-left: 0.375rem;
		}
	}

	/* Arrow button */
	.sapMITBFilterExpandBtn .sapUiIcon {
		color: var(--sapTextColor);
	}
}

/* Head Container - compact */

.sapUiSizeCompact.sapMITH {

	.sapMITBFilterIcon {
		line-height: 1.8rem;
	}

	.sapMITBHead {
		padding-top: 0.875rem;
	}

	&.sapMITBTextOnly {

		.sapMITBHead {
			padding-top: 0.875rem;
		}
	}

	&.sapMITBInLine {

		.sapMITBHead {
			padding-top: 0;
		}
	}

	/* Bottom borders on the currently selected tab - compact */
	&.sapMITBTextOnly .sapMITBVertical.sapMITBFilter .sapMITBContentArrow {
		height: 0.875rem;
	}

	&.sapMITBInLine.sapMITBTextOnly .sapMITBVertical.sapMITBFilter .sapMITBContentArrow {
		height: 0.875rem;
	}

	/* Tabs - compact*/
	.sapMITBAll {

		.sapMITBTab {
			margin-bottom: 0.3rem;
			margin-top: 0.6rem;
		}

		.sapMITBContentArrow {
			height: 1rem;
		}
	}

	.sapMITBHorizontal {

		&.sapMITBFilter .sapMITBHorizontalWrapper .sapMITBText {
			padding-top: 4px;
		}

		&.sapMITBAll .sapMITBTab {
			margin-bottom: 0.125rem;
			margin-top: 0;
		}

		+ .sapMITBSepLine {
			margin-top: 0.125rem;
		}
	}
}

.sapMITBContentArrow {
	position: relative;

	/* creates border bottom with rounded top corners */
	&::after {
		content: '';
		position: absolute;
		bottom: 0;
		left: 0;
		right: 0;
		border-radius: 0.125rem 0.125rem 0 0;
	}
}

