	.sapMITBSelected:not(.sapMITBDisabled) .sapMITBContentArrow::after {
		background: var(--sapShell_Navigation_SelectedColor);
	}

	.sapMITH .sapMITBFilterWithItems .sapMITBFilterExpandBtn {
