	.sapMObjectNumberInner::after {
		left: -0.0625rem;
		top: -0.0625rem;
		right: -0.0625rem;
		bottom: -0.0625rem;
		height: auto;
		outline: none;
		border-radius: 0.25rem;
		border: 0.125rem solid var(--sapContent_FocusColor);
	}

	&.sapMObjectNumberInverted .sapMObjectNumberInner::after {
		left: -0.0625rem;
		top: -0.0625rem;
		right: -0.0625rem;
		bottom: -0.0625rem;
		height: auto;
		outline: none;
		border-radius: var(--sapButton_BorderCornerRadius);
		border: 0.125rem solid var(--sapContent_FocusColor);
	}

	&.sapMObjectNumberLarge {

		.sapMObjectNumberInner::after {
			left: -0.0625rem;
			top: -0.0625rem;
			right: -0.0625rem;
			bottom: -0.0625rem;
			height: auto;
		}

		&.sapMObjectNumberInverted .sapMObjectNumberInner::after {
			left: -0.0625rem;
			top: -0.0625rem;
			right: -0.0625rem;
			bottom: -0.0625rem;
			height: auto;
			outline: none;
			border-radius: var(--sapButton_BorderCornerRadius);
			border: 0.125rem solid var(--sapContent_FocusColor);
		}
	}
