.sapMRI {

	& {
		margin: 0.75rem 0;
	}

	&:not(.sapMRIDisabled):not(.sapMRIDisplayOnly):not(.sapMRIReadOnly):hover {
		opacity: 0.9;
	}

	.sapMRIUnsel {
		color: var(--sapContent_UnratedColor);
	}

	.sapMRISel {
		color: var(--sapContent_RatedColor);
	}
}

.sapMRI.sapMRIDisplayOnly,
.sapMRI.sapMRIReadOnly {

	.sapMRIIconSel {
		color: var(--sapContent_RatedColor);
	}
	.sapMRIUnsel {
		color: var(--sapContent_UnratedColor);
	}
}

.sapMRIDisabled {
	opacity: 0.4;
}

