			bottom: -0.1875rem;
			border: 0.125rem solid var(--sapContent_FocusColor);
			border-radius: 0.125rem;
		}
	}
}
/* END "TileContainer.less" */

/* START "TimePicker.less" */
/* ================================= */
/* CSS for control sap.m/TimePicker  */
/* Evening Horizon theme             */
/* ================================= */

@_sap_m_TimePicker_ItemColor: var(--sapLegend_WorkingBackground);
@_sap_m_TimePicker_ItemHoverColor: var(--sapList_Hover_Background);
@_sap_m_TimePicker_ItemBorderColor: var(--sapList_Background);
/* END "TimePicker.less" */

/* START "TimePickerSliders.less" */
/* ======================================== */
/* CSS for control sap.m/TimePickerSliders  */
/* Evening Horizon theme                    */
/* ======================================== */

/* TimePicker Dropdown */
.sapMTimePickerSlider {

	.sapMTimePickerItem {
		border-radius: var(--sapButton_BorderCornerRadius);
	}
}

.sapMTimePickerContainer {

	.sapMTPColumn:not(:last-child),
	.sapMTPColumn.sapMTPSliderExpanded:not(:last-child) {
		margin-right: 0.5rem;
	}
}

.sapMTimePickerContainer .sapMTPColumn {

	.sapMTimePickerSlider,
	.sapMTimePickerLabel,
	.sapMTimePickerSlider {

		.sapMTimePickerItem,
		.sapMTPPickerSelectionFrame,
		.sapMTPSliderExpanded {
			width: 3rem;
			height: 2.875rem;
		}

		width: 3rem;
		height: 2.875rem;
	}
}

.sapUiSizeCompact .sapMTimePickerContainer .sapMTPColumn {

	.sapMTimePickerSlider,
	.sapMTimePickerLabel,
	.sapMTimePickerSlider {

		.sapMTimePickerItem,
		.sapMTPPickerSelectionFrame,
		.sapMTPSliderExpanded {
			width: 3rem;
		}

		width: 3rem;
		height: 2rem;
	}
}

.sapMTimePickerContainer {

	.sapMTPColumn {

		.sapMTimePickerSlider {

			.sapMTimePickerItem {
				background: var(--sapContent_Selected_Background);
				border: 0.0625rem solid var(--sapList_Background);
				color: var(--sapContent_FocusColor);
				border-color: var(--sapContent_FocusColor);

				&:hover {
					background: var(--sapList_Hover_Background);
					color: var(--sapContent_FocusColor);
				}
			}
		}

		&.sapMTPSliderExpanded {

			.sapMTimePickerSlider {

				.sapMTimePickerItem {
					background: var(--sapLegend_WorkingBackground);
					border: 0.0625rem solid var(--sapList_Background);

					&:hover {
						background: var(--sapList_Hover_Background);
					}

					&:active {
						background: var(--sapContent_Selected_Background);
						color: var(--sapContent_Selected_TextColor);
					}

					&.sapMTimePickerItemSelected {
						background: var(--sapList_Background)
					}
				}

				.sapMTPPickerSelectionFrame {
					border: 0.125rem solid var(--sapList_SelectionBorderColor);
					border-radius: var(--sapButton_BorderCornerRadius);
					outline: none;

					&:hover {

						+ ul > li.sapMTimePickerItem.sapMTimePickerItemSelected {
							background: var(--sapList_Hover_Background);
						}
					}

					&:active {

						& + ul > li.sapMTimePickerItem.sapMTimePickerItemSelected {
							background: var(--sapContent_Selected_Background);
							color: var(--sapContent_Selected_TextColor);
						}
					}
				}
			}
		}
	}

	.sapMTPDisabled {
		opacity: 0.5;
	}
}

/* PHONE STYLES */
.sap-phone {

	/* TimePicker Dropdown */
	.sapMTimePickerContainer {

		.sapMTPColumn {

			.sapMTimePickerSlider,
			.sapMTimePickerLabel,
			.sapMTimePickerSlider {

				.sapMTimePickerItem,
				.sapMTPPickerSelectionFrame,
				.sapMTPSliderExpanded {
					width: 3rem;
					height: 2.875rem;
				}

				width: 3rem;
				height: 2.875rem;
			}

			.sapMTimePickerSlider {

				.sapMTimePickerItem {
					border: 1px solid var(--sapList_Background);
				}
			}

			&.sapMTPSliderExpanded {

				.sapMTimePickerSlider {

					.sapMTimePickerItem {
						background: var(--sapLegend_WorkingBackground);
						border: 1px solid var(--sapList_Background);
						width: 3rem;
					}

					.sapMTPPickerSelectionFrame {
						outline: 0.125rem solid var(--sapSelectedColor);
						outline-offset: -0.188rem;
					}
				}
			}
		}
	}
}
/* END "TimePickerSliders.less" */

/* START "ToggleButton.less" */
/* =================================== */
/* CSS for control sap.m/ToggleButton  */
/* Evening Horizon theme               */
/* =================================== */

html.sap-desktop .sapMToggleBtnPressed,
.sapMToggleBtnPressed:not(.sapMBtnDisabled) {

	&.sapMBtnNegative {
		background-color: var(--sapButton_Reject_Selected_Background);
		border-color: var(--sapButton_Reject_Selected_BorderColor);
		color: var(--sapButton_Reject_Selected_TextColor);

		& .sapUiIcon {
			color: var(--sapButton_Reject_Selected_TextColor);
		}
	}

	&.sapMBtnSuccess {
		background-color: var(--sapButton_Accept_Selected_Background);
		border-color: var(--sapButton_Accept_Selected_BorderColor);
		color: var(--sapButton_Accept_Selected_TextColor);

		& .sapUiIcon {
			color: var(--sapButton_Accept_Selected_TextColor);
		}
	}

	&.sapMBtnCritical {
		background-color: var(--sapButton_Attention_Selected_Background);
		border-color: var(--sapButton_Attention_Selected_BorderColor);
		color: var(--sapButton_Attention_Selected_TextColor);

		& .sapUiIcon {
			color: var(--sapButton_Attention_Selected_TextColor);
		}
	}
}

/* hover and down on toggled button */
.sapMBtn:hover > .sapMBtnHoverable.sapMToggleBtnPressed:not(.sapMBtnActive),
.sapMBtn .sapMBtnHoverable.sapMToggleBtnPressed:hover {
	box-shadow: none;

	&.sapMBtnReject,
	&.sapMBtnNegative {
		background-color: var(--sapButton_Reject_Selected_Hover_Background);
		border-color: var(--sapButton_Reject_Selected_Hover_BorderColor);
		color: var(--sapButton_Reject_Selected_TextColor);

		& .sapUiIcon {
			color: var(--sapButton_Reject_Selected_TextColor);
		}
	}

	&.sapMBtnAccept,
	&.sapMBtnSuccess {
		background-color: var(--sapButton_Accept_Selected_Hover_Background);
		border-color: var(--sapButton_Accept_Selected_Hover_BorderColor);
		color: var(--sapButton_Accept_Selected_TextColor);

		& .sapUiIcon {
			color: var(--sapButton_Accept_Selected_TextColor);
		}
	}

	&.sapMBtnAttention,
	&.sapMBtnCritical {
		background-color: var(--sapButton_Attention_Selected_Hover_Background);
		border-color: var(--sapButton_Attention_Selected_Hover_BorderColor);
		color: var(--sapButton_Attention_Selected_TextColor);

		& .sapUiIcon {
			color: var(--sapButton_Attention_Selected_TextColor);
		}
	}
}

.sapMBtn {

	.sapMBtnCritical,
	.sapMBtnNegative,
	.sapMBtnSuccess,
	.sapMBtnNeutral {

		&.sapMBtnInner.sapMToggleBtnPressed {
			text-shadow: none;
		}
	}
}

/* Focus of toggled button */
.sapMBtn:focus > .sapMBtnInner.sapMToggleBtnPressed.sapMFocusable {
	outline-color: var(--sapContent_FocusColor);
}

.sapMBtn .sapMBtnCritical.sapMToggleBtnPressed,
.sapContrast span.sapMBtnInner.sapMBtnCritical.sapMToggleBtnPressed,
.sapMBtn:hover .sapMBtnCritical.sapMToggleBtnPressed,
.sapContrast:hover span.sapMBtnInner.sapMBtnCritical.sapMToggleBtnPressed,
.sapMBtn .sapMBtnNegative.sapMToggleBtnPressed,
.sapContrast span.sapMBtnInner.sapMBtnNegative.sapMToggleBtnPressed,
.sapMBtn:hover .sapMBtnNegative.sapMToggleBtnPressed,
.sapContrast:hover span.sapMBtnInner.sapMBtnNegative.sapMToggleBtnPressed,
.sapMBtn .sapMBtnSuccess.sapMToggleBtnPressed,
.sapContrast span.sapMBtnInner.sapMBtnSuccess.sapMToggleBtnPressed,
.sapMBtn:hover .sapMBtnSuccess.sapMToggleBtnPressed,
.sapContrast:hover span.sapMBtnInner.sapMBtnSuccess.sapMToggleBtnPressed,
.sapMBtn .sapMBtnNeutral.sapMToggleBtnPressed,
.sapContrast span.sapMBtnInner.sapMBtnNeutral.sapMToggleBtnPressed,
.sapMBtn:hover .sapMBtnNeutral.sapMToggleBtnPressed,
.sapContrast:hover span.sapMBtnInner.sapMBtnNeutral.sapMToggleBtnPressed {

	.sapMBtnContent,
	.sapMBtnIcon {
		color: inherit;
	}
}

.sapMBtn .sapMBtnEmphasized.sapMToggleBtnPressed {
	background-color: var(--sapButton_Selected_Background);
	color: var(--sapButton_Selected_TextColor);
	border-color: var(--sapButton_Selected_BorderColor);
}

span.sapMBtnInner.sapMToggleBtnPressed .sapMBtnIcon,
span.sapMBtnInner.sapMToggleBtnPressed.sapMBtnTransparent > .sapMBtnIcon,
html.sap-desktop .sapMToggleBtnPressed.sapMBtnTransparent > .sapMBtnIcon {
	color: var(--sapButton_Selected_TextColor);
}

html.sap-desktop .sapMBtn:focus > .sapMBtnEmphasized.sapMToggleBtnPressed.sapMFocusable {
	border-color: var(--sapContent_FocusColor);
}

html.sap-desktop .sapMBtn:focus > .sapMBtnEmphasized.sapMToggleBtnPressed.sapMFocusable:not(.sapMBadgeIndicator)::after {
	border-color: var(--sapContent_FocusColor);
	left: -0.0625rem;
	top: -0.0625rem;
	right: -0.0625rem;
	bottom: -0.0625rem;
	border-radius: 0.5rem;
}

html.sap-desktop .sapMBtn:focus > .sapMToggleBtnPressed.sapMBtnEmphasized.sapMFocusable:not(.sapMBtnActive):not(.sapMToggleBtnPressed):not(.sapMBadgeIndicator)::before,
html.sap-desktop .sapMBtn:focus > .sapMToggleBtnPressed.sapMBtnCritical.sapMFocusable:not(.sapMBtnActive):not(.sapMBadgeIndicator)::before,
html.sap-desktop .sapMBtn:focus > .sapMToggleBtnPressed.sapMBtnNegative.sapMFocusable:not(.sapMBtnActive):not(.sapMBadgeIndicator)::before,
html.sap-desktop .sapMBtn:focus > .sapMToggleBtnPressed.sapMBtnSuccess.sapMFocusable:not(.sapMBtnActive):not(.sapMBadgeIndicator)::before {
	border: 0.125rem solid var(--sapContent_FocusColor);
}

.sapUiSizeCompact .sapMBar-CTX .sapMBtnDefault.sapMBtnInner.sapMToggleBtnPressed:not(.sapMBtnDisabled) {
	border-color: var(--sapButton_Selected_BorderColor);
}
/* END "ToggleButton.less" */

/* START "Token.less" */
/* ============================ */
/* CSS for control sap.m/Token  */
/* Evening Horizon theme        */
/* ============================ */

@_sap_m_Token_LeftPadding: 0.3125rem;
@_sap_m_Token_IconWidth: 1.75rem;
@_sap_m_Token_CompactIconWidth: 1.5rem;
@_sap_m_Token_DeleteIconHeight: 2.25rem;
@_sap_m_Token_DeleteIconCompactHeight: 1.625rem;

.sapMToken {
	padding-left: @_sap_m_Token_LeftPadding;
	border-radius: 0.375rem;
	color: var(--sapTextColor);
	font-size: var(--sapFontSize);
	overflow: visible;
}

.sapMTokenSelected,
.sapMToken.sapMTokenReadOnly.sapMTokenSelected {
	background: var(--sapButton_Selected_Background);
	border: var(--sapButton_BorderWidth) solid var(--sapButton_Selected_BorderColor);
	border-radius: 0.375rem;
	font-family: var(--sapFontSemiboldDuplexFamily);
}

.sapMTokenIcon::before {
	display: inline-block;
	width: 0.75rem;
	height: 0.75rem;
	line-height: 1;
}

.sapMToken.sapMTokenSelected:not(.sapMTokenReadOnly)::before {
	content: '';
	position: absolute;
	pointer-events: none;
	z-index: 2;
	bottom: 0;
	left: 0;
	right: 0;
	height: 2px;
	background-color: var(--sapButton_Selected_BorderColor);
}

html.sap-desktop .sapMToken:not(.sapMTokenSelected):not(.sapMTokenReadOnly):not(:focus):hover {
	background: var(--sapButton_Hover_Background);
	border: var(--sapButton_BorderWidth) solid var(--sapButton_TokenBorderColor);
	border-radius: 0.375rem;
}

html.sap-desktop .sapMToken.sapMTokenSelected:not(.sapMTokenReadOnly):hover {
	background: var(--sapButton_Selected_Hover_Background);
	border: var(--sapButton_BorderWidth) solid var(--sapButton_Selected_BorderColor);
}

.sapMToken.sapMTokenReadOnly {
	padding: 0.25rem 0.3125rem;
	color: var(--sapContent_LabelColor);

	.sapMTokenText {
		line-height: 1rem;
	}
}

html.sap-desktop .sapMToken:focus {
	outline: var(--sapContent_FocusWidth) var(--sapContent_FocusStyle) var(--sapContent_FocusColor);
	border-radius: 0.5rem;
	outline-offset: 0.0625rem;
}

html.sap-desktop .sapMToken:not(.sapMTokenSelected):not(.sapMTokenReadOnly):focus:hover {
	border: var(--sapButton_BorderWidth) solid var(--sapButton_TokenBorderColor);
}

html.sap-desktop .sapUiSizeCompact .sapMToken:focus,
html.sap-desktop .sapUiSizeCondensed .sapMToken:focus {
	outline-offset: 0;
}

html.sap-desktop .sapUiSizeCondensed .sapMToken:focus {
	outline-width: 1px;
}

.sapUiSizeCompact .sapMToken.sapMTokenReadOnly {
	padding: 0.125rem 0.25rem;

	.sapMTokenText {
		line-height: 0.875rem;
	}
}

.sapMTokenIcon {
	box-sizing: border-box;
	top: ~"calc((@{_sap_m_Token_TokenHeight} / 2) - (@{_sap_m_Token_DeleteIconHeight} / 2) - @{sapUiButtonBorderWidth})";
	width: @_sap_m_Token_IconWidth;
	height: @_sap_m_Token_DeleteIconHeight;
	display: inline-flex;
	justify-content: center;
	align-items: center;
	right: ~"calc(var(--sapButton_BorderWidth) * -1)";
}

.sapUiSizeCompact .sapMTokenIcon {
	top: ~"calc((@{_sap_m_Token_TokenCompactHeight} / 2) - (@{_sap_m_Token_DeleteIconCompactHeight} / 2) - @{sapUiButtonBorderWidth})";
	width: @_sap_m_Token_CompactIconWidth;
	height: @_sap_m_Token_DeleteIconCompactHeight;
}
/* END "Token.less" */

/* START "Tokenizer.less" */
/* ================================ */
/* CSS for control sap.m/Tokenizer  */
/* Evening Horizon theme            */
/* ================================ */

@_sap_m_Tokenizer_OutlineOffset: 0.3125rem;

.sapMTokenizer {
	padding-left: 0.3125rem;
	position: relative;
	display: inline-block;
	white-space: nowrap;
	overflow: hidden;
	box-sizing: border-box;
}

.sapMTokenizer .sapMToken {
	margin-right: 0.3125rem;
	max-width: calc(~"100% - 0.3125rem");
	margin-top: 0.25rem;
}

html.sap-desktop .sapMTokenizer:focus {
	outline: 2px solid var(--sapContent_FocusColor);
	outline-offset: -0.125rem;
	border-radius: var(--sapField_BorderCornerRadius);
}

/* editable tokens in read-only tokenizer */
.sapMTokenizer.sapMTokenizerReadonly .sapMToken {
	color: var(--sapContent_LabelColor);
}

.sapMTokenizer.sapMTokenizerReadonly .sapMToken.sapMTokenReadOnly {
	padding: 0.25rem 0.3125rem;
}

.sapUiSizeCompact {

	.sapMTokenizer {
		padding-left: 0.25rem;

		&.sapMTokenizerReadonly .sapMToken.sapMTokenReadOnly {
			padding: 0.125rem 0.25rem;
		}

		.sapMToken {
			margin-right: 0.25rem;
		}
	}
}

/* Disabled */

.sapMTokenizer.sapMTokenizerDisabled .sapMToken {

	&:hover,
	&:focus,
	&.sapMTokenSelected,
	&.sapMTokenSelected:focus,
	&.sapMToken.sapMTokenSelected:hover,
	&.sapMToken:not(.sapMTokenSelected):not(:focus):hover {
		background: var(--sapButton_TokenBackground);
		cursor: default;
		color: var(--sapTextColor);
		outline: none;
		border: 1px solid var(--sapButton_TokenBorderColor);
		border-radius: var(--sapButton_BorderCornerRadius);
		box-shadow: none;
	}

	&:focus::after {
		content: none;
	}

	.sapMTokenText,
	.sapMTokenIcon {
		color: var(--sapTextColor);
	}

	.sapMTokenIcon {
		cursor: default;
	}
}

.sapMTokenizer.sapMTokenizerDisabled .sapMTokenizerIndicator {
	cursor: default;
}

.sapMTokenizer.sapMTokenizerReadonly .sapMTokenizerIndicator,
.sapMTokenizer .sapMTokenizerIndicator {
	color: var(--sapLinkColor);
}
/* END "Tokenizer.less" */

/* START "Toolbar.less" */
/* ============================== */
/* CSS for control sap.m/Toolbar  */
/* Evening Horizon theme          */
/* ============================== */

html.sap-desktop .sapMTBActive.sapMTBFocused {
	outline: 0.0625rem dotted var(--sapContent_FocusColor);
	outline-offset: -0.1875rem;
}

.sapMTB {
	height: 2.75rem;
	padding: 0 0.5rem;
}

/* Info Toolbar */
.sapMIBar.sapMTB-Info-CTX.sapMTB {
	padding: 0 0.5rem 0 1rem;
}

/* Info Toolbar Context */
.sapMTB-Info-CTX.sapMTB {
	background: var(--sapInfobar_NonInteractive_Background);
	color: var(--sapList_TextColor);
	height: 2rem;

	> .sapUiIcon {
		width: 1rem;
		height: 1rem;
		color: var(--sapList_TextColor);
	}

	> .sapUiIcon:focus {
		color: var(--sapContent_FocusWidth) var(--sapContent_FocusStyle) var(--sapContent_FocusColor);
	}

	&.sapMTBActive {
		background: var(--sapInfobar_Background);
		color: var(--sapInfobar_TextColor);

		&:active {
			background: var(--sapInfobar_Active_Background);
		}

		&:hover {
			background: var(--sapInfobar_Hover_Background);
		}

		.sapMText,
		.sapMLabel,
		> .sapMTitle {
			color: inherit;
		}
	}
}

html.sap-desktop .sapMTB-Info-CTX.sapMTBActive.sapMTBFocused {
	outline-color: var(--sapContent_ContrastFocusColor);
}

.sapMTB.sapMTBStandard {
	border-bottom: solid 0.0625rem var(--sapGroup_TitleBorderColor);
}

/* solid toolbar context */
.sapMTB-Solid-CTX.sapMTB {
	background-color: var(--sapToolbar_Background);
}

html.sap-desktop .sapMTBActive.sapMTBFocused {
	outline: none;
	box-shadow: inset 0 0 0 var(--sapContent_FocusWidth) var(--sapContent_FocusColor);
}

.sapMTB .sapMTBSeparator {
	background: var(--sapToolbar_SeparatorColor);
}

/* Styles suporting Table inside ObjectPageSubSection scenario */
.sapUxAPObjectPageLayout .sapUiTableOnObjectPageAdjustmentsForSection .sapMIBar.sapMTB.sapMTBHeader-CTX {
	background: var(--sapBackgroundColor);
	padding: 0;
}

.sapUxAPObjectPageLayout.sapUxAPObjectPageLayout-Std-Desktop-XL .sapUiTableOnObjectPageAdjustmentsForSection .sapMIBar.sapMTB.sapMTBHeader-CTX {
	padding: 1rem;
}

.sapUiTableOnObjectPageAdjustmentsForSection .sapMIBar.sapMTB.sapMTBHeader-CTX .sapMBarChildFirstChild {
	margin: 0;
}
/* END "Toolbar.less" */

