/* START "ValueStateHeader.less" */
/* ======================================= */
/* CSS for control sap.m/ValueStateHeader  */
/* Evening Horizon theme                   */
/* ======================================= */

.sapMValueStateHeaderRoot {
	padding: 0.5rem 0.5rem 0.5rem 1.875rem;
	line-height: 1rem;
}

.sapMValueStateHeaderSuccess::before {
	font-family: var(--sapContent_IconFontFamily);
	content: '\e1c1';
	position: absolute;
	top: 0.5rem;
	left: 0;
	vertical-align: middle;
	font-size: 1rem;
	color: var(--sapPositiveElementColor);
	padding-left: 0.5rem;
	padding-right: 0.375rem;
}

.sapMValueStateHeaderError::before {
	font-family: var(--sapContent_IconFontFamily);
	content: '\e1ec';
	position: absolute;
	top: 0.5rem;
	left: 0;
	vertical-align: middle;
	font-size: 1rem;
	color: var(--sapNegativeElementColor);
	padding-left: 0.5rem;
	padding-right: 0.375rem;
}

.sapMValueStateHeaderWarning::before {
	font-family: var(--sapContent_IconFontFamily);
	content: '\e053';
	position: absolute;
	top: 0.5rem;
	left: 0;
	vertical-align: middle;
	font-size: 1rem;
	color: var(--sapCriticalElementColor);
	padding-left: 0.5rem;
	padding-right: 0.375rem;
}

.sapMValueStateHeaderInformation::before {
	font-family: var(--sapContent_IconFontFamily);
	content: '\e289';
	position: absolute;
	top: 0.5rem;
	left: 0;
