						background: @_sap_m_TimePicker_ItemHoverColor;
					}
				}

				&:active {

					& + ul > li.sapMWSItem.sapMWSItemSelected {
						background: var(--sapActiveColor);
						color: var(--sapContent_ContrastTextColor);
					}
