	.sapUxAPObjectChildPage {
		height: @sapUiElementLineHeight;
	}
}

.sapUxAPObjectPageNavigation .sapUxAPITBar.sapUxAPObjectChildPage {
	height: 2.9375rem;
}

.sapUiSizeCompact .sapUxAPObjectPageNavigation .sapUxAPITBar.sapUxAPObjectChildPage {
	height: 1.9375rem;
}

/* ======================================== */
/* Title and Toggle Header Visual Indicator */
/* ======================================== */
.sapUxAPObjectPageLayout {
  &.sapUxAPObjectPageLayoutTitleClickEnabled {

    &.sapUxAPObjectPageLayoutTitleForceHovered .sapFDynamicPageTitle {
      .title-hover(@sapUiObjectHeaderBackground);
	}

	&.sapUxAPObjectPageLayoutTitleForceHovered .sapFDynamicPageTitleTranslucent {
		.title-hover(lighten(@sapUiObjectHeaderBackground, 10));
	}

	&.sapUxAPObjectPageLayoutTitleForceHovered .sapFDynamicPageTitleTransparent {
		.title-hover(transparent);
	}
  }
}

// footer
.sapUxAPObjectPageLayout .sapUxAPObjectPageFooter .sapUxAPObjectPageFloatingFooter {
	background: @sapUiPageFooterBackground;
	color: @sapUiPageFooterTextColor;
	box-shadow: @sapUiShadowLevel1;
	border: none;
	border-radius: @sapUiElementBorderCornerRadius;
}

.title-hover(@backgroundColor) {
	background-color: darken(@backgroundColor, 3);
}


// =====================================
// =====================================
// Delta Styles for Horizon
// =====================================

.sapUxAPObjectPageLayout:not(.sapUxAPObjectPageLayoutNoAnchorBar)>.sapUxAPObjectPageHeaderStickied>.sapFDynamicPageTitle {
    box-shadow: none;
}

.sapUxAPObjectPageLayout .sapMList {
    box-shadow: none;
}


.sapUxAPObjectPageLayout-Std-Phone .sapMSltPressed>.sapMSltArrow, html.sap-phone .sapMSltPressed>.sapMSltArrow {
    border-radius: @sapUiButtonBorderCornerRadius;
    background: @sapUiButtonActiveBackground;
}

// =====================================
// OBJECT PAGE  SMART TEMPLATE CONTACTS ICON
// =====================================

.sapSmartTemplatesObjectPage .sapSmartTemplatesObjectPageContactsImage {
    color: @sapUiContentNonInteractiveIconColor;
}

// =====================================
// Responsive Grid - Object Page spacing fixes
// =====================================
.sapUiRespGrid.sapUiRespGridVSpace1>div {
    margin-bottom: 0.5rem;
}
/* END "ObjectPageLayout.less" */

/* START "ObjectPageSection.less" */
/* =========================================== */
/* CSS for control sap.uxap/ObjectPageSection  */
/* Evening Horizon theme                       */
/* =========================================== */
.sapUxAPObjectPageSection:not(.sapUxAPObjectPageSectionFirstVisible) {
	padding-top: 1.5rem;
}

.sapUxAPObjectPageSection.sapUxAPObjectPageSectionFirstVisible {
	padding-top: 1rem;

	.sapUxAPObjectPageSectionContainer > .sapUiRespGrid > div[class^="sapUiRespGrid"]:first-child > .sapUxAPObjectPageSubSection {
		padding-top: 0;
	}
}

.sapUxAPObjectPageLayout-Std-Desktop .sapUxAPObjectPageSubSectionHeader {
	padding-left: 1rem;
	padding-right: 1.25rem;
}

.sapUiSizeCompact .sapUxAPObjectPageSectionHeader,
.sapUxAPObjectPageSectionHeader {
	height: 2.75rem;
	line-height: 2.25rem;
}

.sapUiSizeCompact .sapUxAPObjectPageSectionTitle,
.sapUxAPObjectPageSectionTitle {
	height: 2.75rem;
	line-height: 2.25rem;
}

.sapUxAPObjectPageSectionHeader,
.sapUxAPObjectPageSubSectionWithSeeMore .sapUxAPSubSectionSeeMoreContainer {
	padding-right: 2.25rem;
}

.sapUxAPObjectPageLayout-Std-Desktop,
.sapUxAPObjectPageLayout-Std-Tablet {
	.sapUxAPObjectPageSectionHeader,
	html .sapUiContainer-Medium .sapUxAPObjectPageSectionHeader,
	.sapUxAPObjectPageSubSectionWithSeeMore .sapUxAPSubSectionSeeMoreContainer {
		padding-right: 1.25rem;
		padding-left: 2rem;
	}
}

.sapUxAPObjectPageLayout-Std-Phone {
	.sapUxAPObjectPageSectionHeader,
	html .sapUiContainer-Narrow .sapUxAPObjectPageSectionHeader,
	.sapUxAPObjectPageSubSectionWithSeeMore .sapUxAPSubSectionSeeMoreContainer {
		padding-right: 1rem;
	}
	.sapUxAPObjectPageSection .sapUxAPObjectPageSectionContainer .sapUxAPObjectPageSubSection .sapUxAPBlockContainer {
		margin-left: 1rem;
		margin-right: 1rem;
	}
}

// =====================================
// =====================================
// Delta Styles for Horizon
// =====================================

// =====================================
// Object Page section container style
// =====================================
.sapUxAPObjectPageSection .sapUxAPObjectPageSectionContainer .sapUxAPObjectPageSubSection .sapUxAPBlockContainer {
    padding-bottom:0.5rem;
    padding-top: 0.5rem;
    padding-left: 0;
    padding-right: 0;
    background: @sapUiGroupContentBackground;
    margin-left: 2rem;
    margin-right: 2rem;
    border-radius: @sapUiElementBorderCornerRadius;
}

// Page Section Focus
.sapUxAPObjectPageSection:focus::before {
	content: '';
	position: absolute;
	display: block;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	border: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiContentFocusColor;
	z-index: 6;
	pointer-events: none;
}

.sapUiSizeCompact .sapUxAPObjectPageSectionTitle {
    padding-bottom: 0;
}

// Styles suporting Table inside ObjectPageSubSection scenario
.sapUiTableOnObjectPageAdjustmentsForSection.sapUxAPObjectPageSection .sapUxAPObjectPageSectionContainer .sapUxAPObjectPageSubSection .sapUxAPBlockContainer {
	background: transparent;
}

.sapUiTableOnObjectPageAdjustmentsForSection .sapUxAPObjectPageSubSection:not(.sapUiAdjustedSectionSubsectionWithoutTable) .sapUxAPBlockContainer > .sapUiRespGrid {
	padding: 0 0;
 }
/* END "ObjectPageSection.less" */

/* START "ObjectPageSubSection.less" */
/* ============================================== */
/* CSS for control sap.uxap/ObjectPageSubSection  */
/* Evening Horizon theme                          */
/* ============================================== */

.sapUxAPObjectPageSubSectionTitleUppercase {
	text-transform: uppercase;
}

.sapUxAPObjectPageLayout-Std-Desktop .sapUxAPBlockContainer {
	padding: 1rem 1rem 2rem 1rem;
}

.sapUiSizeCompact .sapMTB.sapUxAPObjectPageSubSectionHeaderToolbar,
.sapMTB.sapUxAPObjectPageSubSectionHeaderToolbar {
	height: 2.75rem;
}

.sapUxAPObjectPageLayout-Std-Tablet .sapUxAPBlockContainer {
	padding-bottom: 1rem;
}

/* responsive paddings */
.sapUxAPObjectPageLayout.sapUiResponsiveContentPadding {
	&.sapUxAPObjectPageLayout-Std-Tablet .sapUxAPBlockContainer,
	&.sapUxAPObjectPageLayout-Std-Desktop .sapUxAPBlockContainer {
		padding-left: 2rem;
		padding-right: 2rem;
	}
	&.sapUxAPObjectPageLayout-Std-Desktop-XL .sapUxAPBlockContainer {
		padding-left: 3rem;
		padding-right: 3rem;
	}
}

/* compact size */
.sapUiSizeCompact {
	.sapUxAPObjectPageLayout-Std-Tablet .sapUxAPBlockContainerr{
		padding-bottom: 1rem;
	}
}

// =====================================
// =====================================
// Delta Styles for Horizon
// =====================================

// Page Sub Section Focus
.sapUxAPObjectPageSubSectionFocusable:focus .sapUxAPObjectPageSubSectionFocusSpan {
	content: '';
	position: absolute;
	display: block;
	inset: 0;
	border: @sapUiContentFocusWidth @sapUiContentFocusStyle @sapUiContentFocusColor;
	z-index: 6;
	pointer-events: none;
}

.sapUxAPObjectPageLayout-Std-Phone .sapUxAPObjectPageSubSection .sapUiRespGrid.sapUiRespGridHSpace1 {
    padding: 0.5rem 0.5rem 0.5rem 0.5rem;

}

.sapUxAPObjectPageSectionHeader:not(.sapUxAPObjectPageSectionHeaderHidden) + .sapUxAPObjectPageSectionContainer > .sapUiRespGrid >
div[class^="sapUiRespGrid"]:first-child > .sapUxAPObjectPageSubSection:not(.sapUxAPObjectPageSubSectionPromoted) {
	padding-top: 0;
}

// =====================================
// Object Page spacing fixes - To help address the too much white space customer feedback
// =====================================
.sapUxAPObjectPageLayout-Std-Tablet .sapUxAPBlockContainer,
.sapUiSizeCompact .sapUxAPObjectPageLayout-Std-Phone .sapUxAPBlockContainer {
    padding: 0.5rem 0.5rem 0rem 0rem;
}

.sapUiSizeCompact .sapUxAPObjectPageLayout-Std-Desktop .sapUxAPBlockContainer {
    padding: 0.5rem 0.5rem 0.5rem 0.5rem;
}

.sapUxAPObjectPageLayout-Std-Desktop .sapUxAPBlockContainer {
    padding: 0;
}

.sapUxAPBlockContainer .sapUxAPObjectPageSubSectionAlignContent {
	padding: 0 1rem;
}

