Templates.template:
triggerSubmitChanges(
	{
		noBlockUI: ${bNoBlockUI}, // boolean
		noShowResponse: ${bNoShowResponse}, // boolean
		noShowSuccessToast: ${bNoShowSuccessToast}, // boolean
		successMsg: "${successMsg}", // string
		failedMsg: "${failedMsg}", // string
		keepDraftMergeTimer: ${bKeepDraftMergeTimer} // boolean
	}
)

new sap.ui.generic.app.transaction.TransactionController(
	"${oModel}", // sap.ui.model.odata.ODataModel
	"${oQueue}", // sap.ui.generic.app.util.Queue
	{
		noBatchGroups: ${bNoBatchGroups} // boolean
	},
	"${oDraftMergeTimer}" // Object
)
